'use client';

import { BrandMonitor } from '@/components/brand-monitor/brand-monitor';
import { useEffect, useState } from 'react';
import type { ReactElement } from 'react';
import { useRouter, useParams } from 'next/navigation';
import { useTranslations } from 'next-intl';
import { Sparkles, Menu, X, Plus, Trash2, Loader2 } from 'lucide-react';
import { useCustomer, useRefreshCustomer } from '@/hooks/useAutumnCustomer';
import { useBrandAnalyses, useBrandAnalysis, useDeleteBrandAnalysis } from '@/hooks/useBrandAnalyses';
import { Button } from '@/components/ui/button';
import type { BrandAnalysisWithSources } from '@/lib/db/schema';
import { format } from 'date-fns';
import { useSession } from '@/lib/auth-client';
import { ConfirmationDialog } from '@/components/ui/confirmation-dialog';

// Separate component that uses Autumn hooks
function BrandMonitorContent({ session }: { session: any }) {
  const router = useRouter();
  const params = useParams();
  const locale = params.locale as string;
  const t = useTranslations();
  const { customer, isLoading, error } = useCustomer();
  const refreshCustomer = useRefreshCustomer();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [selectedAnalysisId, setSelectedAnalysisId] = useState<string | null>(null);
  const [resetCount, setResetCount] = useState(0);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [analysisToDelete, setAnalysisToDelete] = useState<string | null>(null);
  
  // Queries and mutations
  const { data: analyses, isLoading: analysesLoading } = useBrandAnalyses();
  const { data: currentAnalysis } = useBrandAnalysis(selectedAnalysisId);
  const deleteAnalysis = useDeleteBrandAnalysis();
  const analysesList: BrandAnalysisWithSources[] = analyses ?? [];
  const renderAnalysisItem = (
    item: BrandAnalysisWithSources,
    _index?: number,
    _array?: BrandAnalysisWithSources[]
  ): ReactElement => {
    return (
      <div
        key={item.id}
        className={`p-3 rounded-lg cursor-pointer hover:bg-gray-100 ${
          selectedAnalysisId === item.id ? 'bg-gray-100' : ''
        }`}
        onClick={() => setSelectedAnalysisId(item.id)}
      >
        <div className="flex justify-between items-start">
          <div className="flex-1 min-w-0">
            <p className="font-medium truncate">
              {item.companyName || t('brandMonitor.untitledAnalysis')}
            </p>
            <p className="text-sm text-gray-500 truncate">
              {item.url}
            </p>
            <p className="text-xs text-gray-400">
              {item.createdAt && format(new Date(item.createdAt), 'MMM d, yyyy')}
            </p>
          </div>
          <Button
            size="sm"
            variant="ghost"
            onClick={(e) => {
              e.stopPropagation();
              handleDeleteAnalysis(item.id);
            }}
          >
            <Trash2 className="w-4 h-4" />
          </Button>
        </div>
      </div>
    );
  }
  
  // Get credits from customer data
  const messageUsage = customer?.features?.credits;
  const credits = messageUsage ? (messageUsage.balance || 0) : 0;

  // Determine active plan name/id
  const activeProduct = customer?.products?.find((p: any) => 
    p?.status === 'active' || p?.status === 'trialing' || p?.status === 'past_due'
  );
  // DEV-only plan override via footer select
  const [devPlanOverride, setDevPlanOverride] = useState<string | null>(null);
  useEffect(() => {
    if (process.env.NODE_ENV !== 'development') return;
    const read = () => {
      try {
        const v = localStorage.getItem('devPlanOverride');
        setDevPlanOverride(v);
      } catch {}
    };
    read();
    const handler = () => read();
    window.addEventListener('dev-plan-override-changed', handler);
    return () => window.removeEventListener('dev-plan-override-changed', handler);
  }, []);

  const effectivePlanName: string = ((devPlanOverride && process.env.NODE_ENV === 'development')
    ? devPlanOverride
    : (activeProduct?.name || activeProduct?.id || '')) as string;
  const activePlanName: string = effectivePlanName;
  const isStartPlan = activePlanName.toLowerCase().includes('start') || activePlanName.toLowerCase().includes('free');

  useEffect(() => {
    // If there's an auth error, redirect to login
    if (error?.code === 'UNAUTHORIZED' || error?.code === 'AUTH_ERROR') {
      router.push('/login');
    }
  }, [error, router]);

  const handleCreditsUpdate = async () => {
    // Use the global refresh to update customer data everywhere
    await refreshCustomer();
  };
  
  const handleDeleteAnalysis = async (analysisId: string) => {
    setAnalysisToDelete(analysisId);
    setDeleteDialogOpen(true);
  };

  const confirmDelete = async () => {
    if (analysisToDelete) {
      await deleteAnalysis.mutateAsync(analysisToDelete);
      if (selectedAnalysisId === analysisToDelete) {
        setSelectedAnalysisId(null);
      }
      setAnalysisToDelete(null);
    }
  };
  
  const handleNewAnalysis = () => {
    logger.info('ðŸ†• [BrandMonitorPage] New Analysis button clicked');
    setSelectedAnalysisId(null);
    setResetCount((c) => c + 1);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="flex h-[calc(100vh-4rem)] relative">
        {/* Sidebar Toggle Button - Always visible */}
        <button
          onClick={() => setSidebarOpen(!sidebarOpen)}
          className={`absolute top-2 z-10 p-2 bg-white rounded-lg shadow-md hover:shadow-lg transition-all duration-200 border border-gray-200 ${
            sidebarOpen ? 'left-[324px]' : 'left-4'
          }`}
          aria-label="Toggle sidebar"
        >
          {sidebarOpen ? (
            <X className="h-5 w-5 text-gray-600" />
          ) : (
            <Menu className="h-5 w-5 text-gray-600" />
          )}
        </button>

        {/* Sidebar */}
        <div className={`${sidebarOpen ? 'w-80' : 'w-0'} bg-white border-r overflow-hidden flex flex-col transition-all duration-200`}>
          <div className="p-4 border-b">
            <Button
              onClick={handleNewAnalysis}
              className="w-full btn-firecrawl-orange"
            >
              <Plus className="w-4 h-4 mr-2" />
              {t('brandMonitor.newAnalysis')}
            </Button>
          </div>
          
          <div className="overflow-y-auto flex-1">
            {analysesLoading ? (
              <div className="p-4 text-center text-gray-500">{t('brandMonitor.loadingAnalyses')}</div>
            ) : analysesList.length === 0 ? (
              <div className="p-4 text-center text-gray-500">{t('brandMonitor.noAnalysesYet')}</div>
            ) : (
              <div className="space-y-1 p-2">
                {analysesList.map(renderAnalysisItem)}
              </div>
            )}
          </div>
        </div>

        {/* Main Content */}
        <div className="flex-1 overflow-y-auto">
          <div className="px-6 sm:px-8 lg:px-12 py-8">
            <BrandMonitor 
              key={`brand-monitor-${resetCount}`}
              creditsAvailable={credits} 
              onCreditsUpdate={handleCreditsUpdate}
              selectedAnalysis={selectedAnalysisId ? currentAnalysis : null}
              onSaveAnalysis={() => {
                // This will be called when analysis completes
                // We'll implement this in the next step
              }}
              // UI gating by plan
              hideSourcesTab={isStartPlan}
              hideWebSearchSources={isStartPlan}
            />
          </div>
        </div>
      </div>
      
      <ConfirmationDialog
        open={deleteDialogOpen}
        onOpenChange={setDeleteDialogOpen}
        title={t('brandMonitor.deleteAnalysis')}
        description={t('brandMonitor.deleteAnalysisDesc')}
        confirmText={t('common.delete')}
        cancelText={t('common.cancel')}
        onConfirm={confirmDelete}
        isLoading={deleteAnalysis.isPending}
      />
    </div>
  );
}

import { logger } from '@/lib/logger';

export default function BrandMonitorPage() {
  const { data: session, isPending } = useSession();
  const t = useTranslations();
  const [isClient, setIsClient] = useState(false);

  // Prevent hydration mismatch by ensuring client-side rendering
  useEffect(() => {
    setIsClient(true);
  }, []);

  // Show loading state during hydration and session check
  if (!isClient || isPending) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <Loader2 className="h-8 w-8 animate-spin text-gray-500" />
      </div>
    );
  }

  if (!session) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <p className="text-gray-600">{t('brandMonitor.pleaseLogIn')}</p>
        </div>
      </div>
    );
  }

  return <BrandMonitorContent session={session} />;
}

'use client';

import { useState, useEffect } from 'react';
import { useSession } from '@/lib/auth-client';
import { useRouter, useParams } from 'next/navigation';
import { useTranslations } from 'next-intl';
import { useCustomer } from '@/hooks/useAutumnCustomer';
import { Button } from '@/components/ui/button';
import { Send, Menu, X, MessageSquare, Plus, Trash2 } from 'lucide-react';
import { useConversations, useConversation, useDeleteConversation } from '@/hooks/useConversations';
import { useSendMessage } from '@/hooks/useMessages';
import { format } from 'date-fns';
import { ConfirmationDialog } from '@/components/ui/confirmation-dialog';

// Separate component that uses Autumn hooks
function ChatContent({ session }: { session: any }) {
  const router = useRouter();
  const params = useParams();
  const locale = params.locale as string;
  const t = useTranslations();
  const { allowed, customer, refetch } = useCustomer();
  const [input, setInput] = useState('');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [selectedConversationId, setSelectedConversationId] = useState<string | undefined>(undefined);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [conversationToDelete, setConversationToDelete] = useState<string | undefined>(undefined);
  
  // Queries and mutations
  const { data: conversations, isLoading: conversationsLoading } = useConversations();
  const { data: currentConversation } = useConversation(selectedConversationId ?? null);
  const sendMessage = useSendMessage();
  const deleteConversation = useDeleteConversation();
  
  // Get message usage data
  const messageUsage = customer?.features?.messages;
  const remainingMessages = messageUsage ? (messageUsage.balance || 0) : 0;
  const hasMessages = remainingMessages > 0;
  const isCustomerLoading = !customer && !session; // Still loading customer data

  // Removed auto-scroll functionality

  const handleSendMessage = async () => {
    if (!input.trim() || sendMessage.isPending) return;

    // Check if user has messages available
    if (!allowed({ featureId: 'messages' })) {
      return;
    }

    try {
      const response = await sendMessage.mutateAsync({
        conversationId: selectedConversationId,
        message: input,
      });
      
      setInput('');
      
      // If this created a new conversation, select it
      if (!selectedConversationId && response.conversationId) {
        setSelectedConversationId(response.conversationId);
      }
      
      // Refetch customer data to update credits in navbar
      await refetch();
    } catch (error: any) {
      console.error('Failed to send message:', error);
    }
  };
  
  const handleNewConversation = () => {
    setSelectedConversationId(undefined);
  };
  
  const handleDeleteConversation = async (conversationId: string) => {
    setConversationToDelete(conversationId);
    setDeleteDialogOpen(true);
  };

  const confirmDelete = async () => {
    if (conversationToDelete) {
      await deleteConversation.mutateAsync(conversationToDelete);
      if (selectedConversationId === conversationToDelete) {
        setSelectedConversationId(undefined);
      }
      setConversationToDelete(undefined);
    }
  };

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Sidebar */}
      <div className={`${sidebarOpen ? 'w-64' : 'w-0'} bg-white border-r overflow-hidden flex flex-col transition-all duration-200`}>
        <div className="p-4 border-b">
          <Button
            onClick={handleNewConversation}
            className="w-full btn-firecrawl-orange"
          >
            <Plus className="w-4 h-4 mr-2" />
            {t('chat.newChat')}
          </Button>
        </div>
        
        <div className="overflow-y-auto flex-1">
          {conversationsLoading ? (
            <div className="p-4 text-center text-gray-500">{t('chat.loadingConversations')}</div>
          ) : conversations?.length === 0 ? (
            <div className="p-4 text-center text-gray-500">{t('chat.noConversations')}</div>
          ) : (
            <div className="space-y-1 p-2">
              {conversations?.map((conversation) => (
                <div
                  key={conversation.id}
                  className={`p-3 rounded-lg cursor-pointer hover:bg-gray-100 ${
                    selectedConversationId === conversation.id ? 'bg-gray-100' : ''
                  }`}
                  onClick={() => setSelectedConversationId(conversation.id)}
                >
                  <div className="flex justify-between items-start">
                    <div className="flex-1 min-w-0">
                      <p className="font-medium truncate">
                                                  {conversation.title || t('chat.untitledConversation')}
                      </p>
                      <p className="text-xs text-gray-500">
                        {conversation.lastMessageAt ? format(new Date(conversation.lastMessageAt as unknown as string | number), 'MMM d, h:mm a') : ''}
                      </p>
                    </div>
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteConversation(conversation.id);
                      }}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
        
        <div className="p-4 border-t bg-gray-50">
          <div className="text-sm text-gray-600">
            <p>{t('chat.messagesRemaining')}</p>
            <p className="text-2xl font-bold text-orange-600">{remainingMessages}</p>
          </div>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <div className="bg-white border-b px-4 py-3 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setSidebarOpen(!sidebarOpen)}
            >
              {sidebarOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
            </Button>
            <h1 className="font-semibold">
              {currentConversation?.title || t('chat.newConversation')}
            </h1>
          </div>
        </div>

        {/* Messages Area */}
        <div className="flex-1 overflow-y-auto p-4">
          {isCustomerLoading ? (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mx-auto mb-4"></div>
                <p className="text-gray-600">{t('chat.loadingAccountData')}</p>
              </div>
            </div>
          ) : !hasMessages ? (
            <div className="flex items-center justify-center h-full">
              <div className="text-center max-w-md">
                <MessageSquare className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <h2 className="text-xl font-semibold mb-2">{t('chat.creditBasedMessaging')}</h2>
                <p className="text-gray-600 mb-4">
                  {t('chat.creditBasedDesc')}
                </p>
                <div className="bg-orange-50 border border-orange-200 rounded-lg p-4 mb-4">
                  <p className="text-sm text-orange-800">
                    {t('chat.youCurrentlyHave')} <span className="font-bold">{remainingMessages}</span> {t('chat.messageCreditsAvailable')}
                  </p>
                </div>
                <Button
                  onClick={() => router.push(`/${locale}/plans`)}
                  className="btn-firecrawl-orange"
                >
                  {t('chat.getMoreCredits')}
                </Button>
              </div>
            </div>
          ) : currentConversation?.messages && currentConversation.messages.length > 0 ? (
            <div className="space-y-4 mb-20">
              {currentConversation.messages.map((message) => (
                <div
                  key={message.id}
                  className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                >
                  <div
                    className={`max-w-[70%] rounded-lg px-4 py-2 ${
                      message.role === 'user'
                        ? 'bg-orange-500 text-white'
                        : 'bg-gray-100 text-gray-900'
                    }`}
                  >
                    <p className="whitespace-pre-wrap">{message.content}</p>
                    <p className={`text-xs mt-1 ${
                      message.role === 'user' ? 'text-orange-100' : 'text-gray-500'
                    }`}>
                      {message.createdAt ? format(new Date(message.createdAt as unknown as string | number), 'h:mm a') : ''}
                    </p>
                  </div>
                </div>
              ))}
              {sendMessage.isPending && (
                <div className="flex justify-start">
                  <div className="bg-gray-100 text-gray-900 rounded-lg px-4 py-2">
                    <div className="flex space-x-1">
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" />
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }} />
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }} />
                    </div>
                  </div>
                </div>
              )}
            </div>
          ) : (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <MessageSquare className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <h2 className="text-xl font-semibold mb-2">{t('chat.startConversation')}</h2>
                <p className="text-gray-600">
                  {t('chat.sendMessageToBegin')}
                </p>
              </div>
            </div>
          )}
        </div>

        {/* Input Area */}
        <div className="border-t bg-white p-4">
          <form
            onSubmit={(e) => {
              e.preventDefault();
              handleSendMessage();
            }}
            className="flex gap-2"
          >
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder={hasMessages ? t('chat.typeMessage') : t('chat.noMessagesAvailable')}
              disabled={!hasMessages || sendMessage.isPending}
              className="flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500 disabled:bg-gray-100 disabled:text-gray-500"
            />
            <Button
              type="submit"
              disabled={!hasMessages || !input.trim() || sendMessage.isPending}
              className="btn-firecrawl-orange"
            >
              <Send className="w-4 h-4" />
            </Button>
          </form>
        </div>
      </div>
      
      <ConfirmationDialog
        open={deleteDialogOpen}
        onOpenChange={setDeleteDialogOpen}
        title={t('chat.deleteConversation')}
        description={t('chat.deleteConversationDesc')}
        confirmText={t('chat.delete')}
        cancelText={t('common.cancel')}
        onConfirm={confirmDelete}
        isLoading={deleteConversation.isPending}
      />
    </div>
  );
}

export default function ChatPage() {
  const { data: session, isPending } = useSession();
  const router = useRouter();

  useEffect(() => {
    if (!isPending && !session) {
      router.push('/login');
    }
  }, [session, isPending, router]);

  if (isPending || !session) {
    return (
      <div className="flex h-screen items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  return <ChatContent session={session} />;
}

'use client';

import { useCustomer } from '@/hooks/useAutumnCustomer';
import { usePricingTable } from 'autumn-js/react';
import { useSession } from '@/lib/auth-client';
import { useRouter, useParams } from 'next/navigation';
import { useEffect, useState } from 'react';
import { useTranslations } from 'next-intl';
import { Lock, CheckCircle, AlertCircle, Loader2, User, Mail, Phone, Edit2, Save, X } from 'lucide-react';
import { Button } from '@/components/ui/button';
import ProductChangeDialog from '@/components/autumn/product-change-dialog';
import { useProfile, useUpdateProfile, useSettings, useUpdateSettings } from '@/hooks/useProfile';

// Separate component that uses Autumn hooks
function DashboardContent({ session }: { session: any }) {
  const { customer, attach } = useCustomer();
  const { products } = usePricingTable();
  const [loadingProductId, setLoadingProductId] = useState<string | null>(null);
  const t = useTranslations();
  const params = useParams();
  const locale = params.locale as string;
  
  // Profile and settings hooks
  const { data: profileData } = useProfile();
  const updateProfile = useUpdateProfile();
  const { data: settings } = useSettings();
  const updateSettings = useUpdateSettings();
  
  // Profile edit state
  const [isEditingProfile, setIsEditingProfile] = useState(false);
  const [profileForm, setProfileForm] = useState({
    displayName: '',
    bio: '',
    phone: '',
  });

  useEffect(() => {
    if (profileData?.profile) {
      setProfileForm({
        displayName: profileData.profile.displayName || '',
        bio: profileData.profile.bio || '',
        phone: profileData.profile.phone || '',
      });
    }
  }, [profileData]);

  const handleSaveProfile = async () => {
    await updateProfile.mutateAsync(profileForm);
    setIsEditingProfile(false);
  };

  const handleCancelEdit = () => {
    setIsEditingProfile(false);
    if (profileData?.profile) {
      setProfileForm({
        displayName: profileData.profile.displayName || '',
        bio: profileData.profile.bio || '',
        phone: profileData.profile.phone || '',
      });
    }
  };

  const handleSettingToggle = async (key: string, value: boolean) => {
    await updateSettings.mutateAsync({ [key]: value });
  };

  // Get current user's products and features
  const userProducts = customer?.products || [];
  const userFeatures = customer?.features || {};
  
  // Find the actual active product (not scheduled)
  const activeProduct = userProducts.find(p => 
    p.status === 'active' || p.status === 'trialing' || p.status === 'past_due'
  );
  const scheduledProduct = userProducts.find(p => 
    p.status === 'scheduled' || (p.started_at && new Date(p.started_at) > new Date())
  );

  const handleUpgrade = async (productId: string) => {
    try {
      setLoadingProductId(productId);
      await attach({
        productId,
        dialog: ProductChangeDialog,
      });
    } finally {
      setLoadingProductId(null);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold mb-8">{t('dashboard.title')}</h1>

        {/* Profile Section */}
        <div className="bg-white rounded-lg shadow p-6 mb-8">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-semibold">{t('dashboard.profileInformation')}</h2>
            {!isEditingProfile ? (
              <Button
                onClick={() => setIsEditingProfile(true)}
                size="sm"
                className="bg-black text-white hover:bg-gray-800"
              >
                <Edit2 className="h-4 w-4 mr-2" />
                {t('dashboard.editProfile')}
              </Button>
            ) : (
              <div className="flex gap-2">
                <Button
                  onClick={handleSaveProfile}
                  size="sm"
                  variant="default"
                  disabled={updateProfile.isPending}
                >
                  <Save className="h-4 w-4 mr-2" />
                  {t('dashboard.save')}
                </Button>
                <Button
                  onClick={handleCancelEdit}
                  size="sm"
                  variant="outline"
                  disabled={updateProfile.isPending}
                >
                  <X className="h-4 w-4 mr-2" />
                  {t('dashboard.cancel')}
                </Button>
              </div>
            )}
          </div>
          
          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                <Mail className="inline-block h-4 w-4 mr-1" />
                {t('dashboard.email')}
              </label>
              <p className="text-gray-900">{session.user?.email}</p>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                <User className="inline-block h-4 w-4 mr-1" />
                {t('dashboard.displayName')}
              </label>
              {isEditingProfile ? (
                <input
                  type="text"
                  value={profileForm.displayName}
                  onChange={(e) => setProfileForm({ ...profileForm, displayName: e.target.value })}
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder={t('dashboard.enterDisplayName')}
                />
              ) : (
                <p className="text-gray-900">
                  {profileData?.profile?.displayName || t('dashboard.notSet')}
                </p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                <Phone className="inline-block h-4 w-4 mr-1" />
                {t('dashboard.phone')}
              </label>
              {isEditingProfile ? (
                <input
                  type="tel"
                  value={profileForm.phone}
                  onChange={(e) => setProfileForm({ ...profileForm, phone: e.target.value })}
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder={t('dashboard.enterPhone')}
                />
              ) : (
                <p className="text-gray-900">
                  {profileData?.profile?.phone || t('dashboard.notSet')}
                </p>
              )}
            </div>
            
            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                {t('dashboard.bio')}
              </label>
              {isEditingProfile ? (
                <textarea
                  value={profileForm.bio}
                  onChange={(e) => setProfileForm({ ...profileForm, bio: e.target.value })}
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  rows={3}
                  placeholder={t('dashboard.tellAboutYourself')}
                />
              ) : (
                <p className="text-gray-900">
                  {profileData?.profile?.bio || t('dashboard.notSet')}
                </p>
              )}
            </div>
          </div>
        </div>

        {/* Available Plans */}
        <div className="bg-white rounded-lg shadow p-6 mb-8">
          <h2 className="text-xl font-semibold mb-4">{t('dashboard.availablePlans')}</h2>
          {!products ? (
            <div className="flex justify-center py-8">
              <Loader2 className="h-6 w-6 animate-spin text-gray-400" />
            </div>
          ) : (
            <div className="space-y-4">
              {(() => {
                const findProductDetails = (id?: string) => (products || []).find((p: any) => p.id === id);
                const parseCreditsFromDisplay = (p: any): number => {
                  const items = p?.items || [];
                  for (const it of items) {
                    const text: string | undefined = it?.display?.primary_text;
                    if (!text) continue;
                    const match = text.match(/([0-9][0-9\s,\.]*)\s*credits?/i);
                    if (match && match[1]) {
                      const normalized = match[1].replace(/[\s,]/g, '');
                      const num = Number(normalized);
                      if (!Number.isNaN(num)) return num;
                    }
                  }
                  return 0;
                };
                const getIncludedCredits = (p: any) => {
                  if (!p) return 0;
                  const unitItem = p?.items?.find((it: any) => it.type === 'unit' && it.unit?.quantity !== undefined);
                  if (unitItem?.unit?.quantity !== undefined && unitItem.unit.quantity !== null) {
                    return unitItem.unit.quantity as number;
                  }
                  return parseCreditsFromDisplay(p);
                };
                const activeProductDetails = findProductDetails(activeProduct?.id);
                const activeCredits = activeProductDetails ? getIncludedCredits(activeProductDetails) : 0;

                return products
                  .filter((product) => product.id !== 'free' && product.id !== '50')
                  .map((product) => {
                const isCurrentPlan = activeProduct?.id === product.id;
                const isScheduledPlan = scheduledProduct?.id === product.id;
                const features = product.properties?.is_free ? product.items : product.items?.slice(1) || [];

                  const productCredits = getIncludedCredits(product);
                  const isLowerTierThanActive = !!activeProduct && productCredits < activeCredits;
                  const isFreePlan = product.properties?.is_free || product.id === 'free';

                  return (
                  <div key={product.id} className="border rounded-lg p-4">
                    <div className="flex justify-between items-start">
                      <div className="flex-1">
                        <h3 className="font-medium text-lg">
                          {product.display?.name || product.name}
                          {isCurrentPlan && (
                            <span className="ml-2 text-sm text-green-600">{t('dashboard.currentPlanLabel')}</span>
                          )}
                          {isScheduledPlan && (
                            <span className="ml-2 text-sm text-orange-600">{t('dashboard.scheduledLabel')}</span>
                          )}
                        </h3>
                        {product.display?.description && (
                          <p className="text-sm text-gray-600 mt-1">{product.display.description}</p>
                        )}
                        <ul className="mt-3 space-y-1">
                          {features.slice(0, 3).map((item, index) => (
                            <li key={index} className="flex items-start text-sm">
                              {isCurrentPlan ? (
                                <CheckCircle className="h-4 w-4 text-green-500 mr-2 flex-shrink-0 mt-0.5" />
                              ) : (
                                <Lock className="h-4 w-4 text-gray-400 mr-2 flex-shrink-0 mt-0.5" />
                              )}
                              <span className={!isCurrentPlan ? 'text-gray-500' : ''}>
                                {item.display?.primary_text}
                              </span>
                            </li>
                          ))}
                        </ul>
                      </div>
                      {!isCurrentPlan && !isScheduledPlan && !isFreePlan && (
                        <Button 
                          onClick={() => handleUpgrade(product.id)} 
                          size="sm"
                          variant="outline"
                          disabled={loadingProductId !== null}
                        >
                          {loadingProductId === product.id ? (
                            <>
                              <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                              {t('dashboard.loading')}
                            </>
                          ) : (
                            isLowerTierThanActive ? t('dashboard.downgrade') : t('dashboard.upgrade')
                          )}
                        </Button>
                      )}
                      {isScheduledPlan && (() => {
                        const scheduledStart = scheduledProduct?.started_at || scheduledProduct?.current_period_end;
                        if (!scheduledStart) return null;
                        return (
                          <span className="text-sm text-gray-500">
                            {t('dashboard.starts')} {new Date(scheduledStart).toLocaleDateString()}
                          </span>
                        );
                      })()}
                    </div>
                  </div>
                  );
                });
              })()}
            </div>
          )}
        </div>

        {/* Usage Stats */}
        <div className="bg-white rounded-lg shadow p-6 mb-8">
          <h2 className="text-xl font-semibold mb-4">{t('dashboard.usageStatistics')}</h2>
          {Object.keys(userFeatures).length > 0 ? (
            <div className="space-y-4">
              {Object.entries(userFeatures).map(([featureId, feature]) => (
                <div key={featureId}>
                  <div className="mb-4">
                    <h3 className="font-medium mb-2 capitalize">{featureId.replace(/_/g, ' ')}</h3>
                    <div className="flex justify-between text-sm mb-1">
                      <span>{t('dashboard.used')}</span>
                      <span>{(feature.usage || 0)} / {(feature.included_usage ?? ((feature.balance ?? 0) + (feature.usage || 0)))}</span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div
                        className="bg-orange-500 h-2 rounded-full transition-all"
                        style={{
                          width: `${Math.min(((feature.usage || 0) / ((feature.included_usage ?? ((feature.balance ?? 0) + (feature.usage || 0))) || 1)) * 100, 100)}%`
                        }}
                      />
                    </div>
                  </div>
                  {feature.next_reset_at ? (
                    <p className="text-sm text-gray-600">
                      {t('dashboard.resetsOn')}: {new Date(feature.next_reset_at).toLocaleDateString()}
                    </p>
                  ) : null}
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-500">{t('dashboard.noUsageData')}</p>
          )}
        </div>

        {/* Settings Section */}
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold mb-4">{t('dashboard.settings')}</h2>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="font-medium">{t('dashboard.emailNotifications')}</p>
                <p className="text-sm text-gray-600">{t('dashboard.emailNotificationsDesc')}</p>
              </div>
              <button
                onClick={() => handleSettingToggle('emailNotifications', !settings?.emailNotifications)}
                className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${
                  settings?.emailNotifications ? 'bg-orange-500' : 'bg-gray-200'
                }`}
                disabled={updateSettings.isPending}
              >
                <span
                  className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                    settings?.emailNotifications ? 'translate-x-6' : 'translate-x-1'
                  }`}
                />
              </button>
            </div>
            
            <div className="flex items-center justify-between">
              <div>
                <p className="font-medium">{t('dashboard.marketingEmails')}</p>
                <p className="text-sm text-gray-600">{t('dashboard.marketingEmailsDesc')}</p>
              </div>
              <button
                onClick={() => handleSettingToggle('marketingEmails', !settings?.marketingEmails)}
                className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${
                  settings?.marketingEmails ? 'bg-orange-500' : 'bg-gray-200'
                }`}
                disabled={updateSettings.isPending}
              >
                <span
                  className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                    settings?.marketingEmails ? 'translate-x-6' : 'translate-x-1'
                  }`}
                />
              </button>
            </div>
          </div>
        </div>

      </div>
    </div>
  );
}

export default function DashboardPage() {
  const { data: session, isPending } = useSession();
  const router = useRouter();
  const t = useTranslations();
  const params = useParams();
  const locale = params.locale as string;

  useEffect(() => {
    if (!isPending && !session) {
      router.push(`/${locale}/login`);
    }
  }, [session, isPending, router, locale]);

  if (isPending || !session) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mx-auto mb-4"></div>
          <p className="text-gray-600">{t('dashboard.loading')}</p>
        </div>
      </div>
    );
  }

  // Only render DashboardContent when we have a session and AutumnProvider is available
  return <DashboardContent session={session} />;
}

"use client";
import { useEffect, useState } from 'react';
import { useParams } from 'next/navigation';
import { marked } from 'marked';

const sectionsConfig = [
  { id: 'introduction', fr: '/docs/introduction/fr.md', en: '/docs/introduction/en.md', label: { fr: 'Introduction', en: 'Introduction' } },
  { id: 'features', fr: '/docs/key-features/fr.md', en: '/docs/key-features/en.md', label: { fr: 'FonctionnalitÃ©s clÃ©s', en: 'Key features' } },
  { id: 'getting-started', fr: '/docs/getting-started/fr.md', en: '/docs/getting-started/en.md', label: { fr: 'Bien dÃ©marrer', en: 'Getting started' } },
  { id: 'prompts', fr: '/docs/prompts/fr.md', en: '/docs/prompts/en.md', label: { fr: 'Prompts efficaces', en: 'Effective prompts' } },
  { id: 'reading-results', fr: '/docs/reading-results/fr.md', en: '/docs/reading-results/en.md', label: { fr: 'Lire les rÃ©sultats', en: 'Reading results' } },
  { id: 'credits', fr: '/docs/credits/fr.md', en: '/docs/credits/en.md', label: { fr: 'CrÃ©dits', en: 'Credits' } },
  { id: 'openai-web-search', fr: '/docs/openai-web-search/fr.md', en: '/docs/openai-web-search/en.md', label: { fr: 'OpenAI Web Search', en: 'OpenAI Web Search' } },
];

export default function DocsPage() {
  const routeParams = useParams<{ locale?: string }>();
  const [active, setActive] = useState<string>('introduction');
  const [htmlById, setHtmlById] = useState<Record<string, string>>({});
  const [error, setError] = useState<string>("");
  const [locale, setLocale] = useState<'fr'|'en'>(routeParams?.locale === 'en' ? 'en' : 'fr');

  useEffect(() => {
    const l = window.location.pathname.split('/')[1] || 'fr';
    setLocale((['fr','en'] as const).includes(l as any) ? (l as 'fr'|'en') : 'fr');
  }, []);

  useEffect(() => {
    let cancelled = false;
    async function loadAll() {
      try {
        const { default: DOMPurify } = await import('isomorphic-dompurify');
        const entries = await Promise.all(
          sectionsConfig.map(async (s) => {
            const path = locale === 'fr' ? s.fr : s.en;
            const res = await fetch(path, { cache: 'no-store' });
            if (!res.ok) throw new Error(`HTTP ${res.status} for ${path}`);
            const md = await res.text();
            const parsed = marked.parse(md) as string;
            const clean = DOMPurify.sanitize(parsed);
            return [s.id, clean as string] as const;
          })
        );
        if (!cancelled) {
          const map: Record<string,string> = {};
          for (const [k,v] of entries) map[k] = v;
          setHtmlById(map);
        }
      } catch (e: any) {
        if (!cancelled) setError(e?.message || 'Failed to load docs');
      }
    }
    loadAll();
    return () => { cancelled = true; };
  }, [locale]);

  const labelFor = (id: string) => {
    const s = sectionsConfig.find(x => x.id === id)!;
    return s.label[locale as 'fr'|'en'];
  };

  return (
    <div className="w-full">
      <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8 py-10">
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-8">
          <aside className="lg:col-span-3">
            <nav className="sticky top-24 space-y-1">
              {sectionsConfig.map(s => (
                <a
                  key={s.id}
                  href={`#${s.id}`}
                  onClick={() => setActive(s.id)}
                  className={`block w-full text-left rounded-md px-3 py-2 text-sm transition-colors ${active===s.id? 'bg-zinc-100 text-zinc-900 dark:bg-zinc-800 dark:text-zinc-100':'text-zinc-600 hover:text-zinc-900 hover:bg-zinc-50 dark:text-zinc-300 dark:hover:text-zinc-50 dark:hover:bg-zinc-900'}`}
                ><h2>{labelFor(s.id)}</h2></a>
              ))}
            </nav>
          </aside>
          <section className="lg:col-span-9 min-w-0">
            {error ? (
              <div className="prose prose-zinc dark:prose-invert max-w-none">{error}</div>
            ) : (
              <div className="prose prose-zinc dark:prose-invert max-w-none">
                {sectionsConfig.map(s => (
                  <article key={s.id} id={s.id} className="scroll-mt-24">
                    <h1 className="text-2xl font-semibold mb-2">{labelFor(s.id)}</h1>
                    <div dangerouslySetInnerHTML={{ __html: htmlById[s.id] || '' }} />
                  </article>
                ))}
              </div>
            )}
          </section>
        </div>
      </div>
    </div>
  );
}

