import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@/lib/auth';
import { db } from '@/lib/db';
import { messageFeedback, messages } from '@/lib/db/schema';
import { and, eq } from 'drizzle-orm';

// POST /api/chat/feedback - Submit feedback for a message
export async function POST(request: NextRequest) {
  try {
    const session = await auth.api.getSession({
      headers: request.headers,
    });

    if (!session?.user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { messageId, helpful, rating, feedback } = await request.json();

    if (!messageId) {
      return NextResponse.json({ error: 'Message ID required' }, { status: 400 });
    }

    // Verify the message exists and belongs to the user
    const message = await db.query.messages.findFirst({
      where: and(
        eq(messages.id, messageId),
        eq(messages.userId, session.user.id)
      ),
    });

    if (!message) {
      return NextResponse.json({ error: 'Message not found' }, { status: 404 });
    }

    // Create or update feedback
    const existingFeedback = await db.query.messageFeedback.findFirst({
      where: and(
        eq(messageFeedback.messageId, messageId),
        eq(messageFeedback.userId, session.user.id)
      ),
    });

    if (existingFeedback) {
      // Update existing feedback
      const [updated] = await db
        .update(messageFeedback)
        .set({
          rating: rating || (helpful === true ? 5 : helpful === false ? 1 : existingFeedback.rating),
          feedback: feedback || existingFeedback.feedback,
        })
        .where(eq(messageFeedback.id, existingFeedback.id))
        .returning();

      return NextResponse.json(updated);
    } else {
      // Create new feedback
      const [created] = await db
        .insert(messageFeedback)
        .values({
          messageId,
          userId: session.user.id,
          rating: rating || (helpful === true ? 5 : helpful === false ? 1 : null),
          feedback,
        })
        .returning();

      return NextResponse.json(created);
    }
  } catch (error: any) {
    console.error('Feedback POST error:', error);
    return NextResponse.json(
      { error: error.message || 'Internal server error' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@/lib/auth';
import { db } from '@/lib/db';
import { userSettings } from '@/lib/db/schema';
import { eq } from 'drizzle-orm';
import { handleApiError, AuthenticationError, ValidationError } from '@/lib/api-errors';

// GET /api/user/settings - Get user settings
export async function GET(request: NextRequest) {
  try {
    const session = await auth.api.getSession({
      headers: request.headers,
    });

    if (!session?.user) {
      throw new AuthenticationError('Please log in to access settings');
    }

    const settings = await db.query.userSettings.findFirst({
      where: eq(userSettings.userId, session.user.id),
    });

    // Return default settings if none exist
    if (!settings) {
      return NextResponse.json({
        theme: 'light',
        emailNotifications: true,
        marketingEmails: false,
        defaultModel: 'gpt-3.5-turbo',
        metadata: {},
      });
    }

    return NextResponse.json(settings);
  } catch (error) {
    return handleApiError(error);
  }
}

// PUT /api/user/settings - Update user settings
export async function PUT(request: NextRequest) {
  try {
    const session = await auth.api.getSession({
      headers: request.headers,
    });

    if (!session?.user) {
      throw new AuthenticationError('Please log in to access settings');
    }

    const data = await request.json();
    const { theme, emailNotifications, marketingEmails, defaultModel, metadata } = data;

    // Validate theme
    if (theme && !['light', 'dark'].includes(theme)) {
      return NextResponse.json({ error: 'Invalid theme value' }, { status: 400 });
    }

    // Upsert user settings
    const [updatedSettings] = await db
      .insert(userSettings)
      .values({
        userId: session.user.id,
        theme: theme || 'light',
        emailNotifications: emailNotifications ?? true,
        marketingEmails: marketingEmails ?? false,
        defaultModel: defaultModel || 'gpt-3.5-turbo',
        metadata: metadata || {},
      })
      .onConflictDoUpdate({
        target: userSettings.userId,
        set: {
          theme,
          emailNotifications,
          marketingEmails,
          defaultModel,
          metadata,
          updatedAt: new Date(),
        },
      })
      .returning();

    return NextResponse.json(updatedSettings);
  } catch (error) {
    return handleApiError(error);
  }
}

// PATCH /api/user/settings - Partial update
export async function PATCH(request: NextRequest) {
  try {
    const session = await auth.api.getSession({
      headers: request.headers,
    });

    if (!session?.user) {
      throw new AuthenticationError('Please log in to access settings');
    }

    const updates = await request.json();
    
    // Remove any fields that shouldn't be updated
    delete updates.id;
    delete updates.userId;
    delete updates.createdAt;

    // Validate theme if provided
    if (updates.theme && !['light', 'dark'].includes(updates.theme)) {
      return NextResponse.json({ error: 'Invalid theme value' }, { status: 400 });
    }

    if (Object.keys(updates).length === 0) {
      return NextResponse.json({ error: 'No valid fields to update' }, { status: 400 });
    }

    // Check if settings exist
    const existingSettings = await db.query.userSettings.findFirst({
      where: eq(userSettings.userId, session.user.id),
    });

    if (!existingSettings) {
      // Create new settings with partial data
      const [newSettings] = await db
        .insert(userSettings)
        .values({
          userId: session.user.id,
          ...updates,
        })
        .returning();
      
      return NextResponse.json(newSettings);
    }

    // Update existing settings
    const [updatedSettings] = await db
      .update(userSettings)
      .set({
        ...updates,
        updatedAt: new Date(),
      })
      .where(eq(userSettings.userId, session.user.id))
      .returning();

    return NextResponse.json(updatedSettings);
  } catch (error) {
    return handleApiError(error);
  }
}

