                ${isExpanded ? 'max-h-[4000px] opacity-100' : 'max-h-0 opacity-0'}
              `}
              onClick={(e) => e.stopPropagation()}
            >
              <div className="border-t border-gray-100 px-3 py-3">
                {promptResponses.length > 0 ? (
                  <div className="space-y-4">
                    {['OpenAI', 'Anthropic', 'Google', 'Perplexity'].map((providerName) => {
                      const response = promptResponses.find(r => r.provider === providerName);
                      if (!response) return null;
                      
                      // Check if response failed (empty response text)
                      const isFailed = !response.response || response.response.trim().length === 0;
                      
                      return (
                      <div key={providerName} className="space-y-1">
                        <div className="flex items-center gap-2">
                          <div className="flex items-center gap-1">
                            {getProviderIcon(response.provider)}
                            <span className="font-medium text-sm text-gray-900">{response.provider}</span>
                          </div>
                          {isFailed ? (
                            <Badge variant="destructive" className="text-xs bg-red-100 text-red-800">
                              Failed Ã—
                            </Badge>
                          ) : response.brandMentioned ? (
                            <Badge variant="default" className="text-xs bg-green-100 text-green-800">
                              Brand Mentioned
                            </Badge>
                          ) : null}
                          {response.brandPosition && response.brandPosition > 0 && (
                            <Badge variant="outline" className="text-xs">
                              Position #{response.brandPosition}
                            </Badge>
                          )}
                        </div>
                        <div className="bg-gray-50 rounded-md p-3 text-sm text-gray-700 select-text cursor-text max-w-full overflow-hidden">
                          {isFailed ? (
                            <div className="text-red-600 italic">
                              Response failed or returned empty content
                            </div>
                          ) : (
                            <HighlightedResponse
                              response={response}
                              brandName={brandName}
                              competitors={competitors}
                              showHighlighting={true}
                              renderMarkdown={true}
                            />
                          )}
                        </div>
                        
                        {/* Web Search Sources */}
                        {!hideWebSearchSources && response.webSearchSources && response.webSearchSources.length > 0 && (
                          <div className="mt-2 p-2 bg-blue-50 border border-blue-200 rounded-md">
                            <div className="flex items-center gap-1 mb-2">
                              <svg className="w-4 h-4 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                              </svg>
                              <span className="text-xs font-medium text-blue-800">Sources consultÃ©es</span>
                            </div>
                            <div className="space-y-1">
                              {(() => {
                                const expandKey = `${idx}-${providerName}`;
                                const toDomain = (input: string): string | null => {
                                  if (!input) return null;
                                  try {
                                    const maybeUrl = /^(https?:)?\/\//i.test(input) ? input : `https://${input}`;
                                    const { hostname } = new URL(maybeUrl);
                                    return hostname.replace(/^www\./i, '');
                                  } catch {
                                    // Fallback: strip path if any
                                    return input.split('/')[0].replace(/^www\./i, '').trim() || null;
                                  }
                                };
                                const domains = Array.from(new Set(
                                  (response.webSearchSources || [])
                                    .map((s: any) => toDomain(
                                      typeof s?.domain === 'string' && s.domain.trim()
                                        ? s.domain
                                        : (typeof s?.url === 'string' ? s.url : '')
                                    ))
                                    .filter(Boolean)
                                )) as string[];

                                const showAll = !!expandedSources[expandKey];
                                const visible = showAll ? domains : domains.slice(0, 3);
                                return (
                                  <>
                                    {visible.map((domain) => (
                                      <div key={domain} className="text-xs text-blue-700">
                                        <a
                                          href={`https://${domain}`}
                                          target="_blank"
                                          rel="noopener noreferrer"
                                          className="hover:text-blue-900 underline"
                                        >
                                          {domain}
                                        </a>
                                      </div>
                                    ))}
                                    {domains.length > 3 && (
                                      showAll ? (
                                        <button
                                          type="button"
                                          onClick={() => setExpandedSources(prev => ({ ...prev, [expandKey]: false }))}
                                          className="text-xs text-blue-600 italic underline"
                                        >
                                          {t('sourcesTab.showLess')}
                                        </button>
                                      ) : (
                                        <button
                                          type="button"
                                          onClick={() => setExpandedSources(prev => ({ ...prev, [expandKey]: true }))}
                                          className="text-xs text-blue-600 italic underline"
                                        >
                                          +{domains.length - 3} {t('sourcesTab.showMore')}
                                        </button>
                                      )
                                    )}
                                  </>
                                );
                              })()}
                            </div>
                          </div>
                        )}
                      </div>
                      );
                    })}
                  </div>
                ) : (
                  <div className="text-center py-6 bg-red-50 border border-red-200 rounded-lg">
                    <div className="flex items-center justify-center gap-2 mb-2">
                      <svg className="w-5 h-5 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
                      </svg>
                      <span className="text-red-800 font-medium">Aucune rÃ©ponse disponible</span>
                    </div>
                    <p className="text-red-700 text-sm mb-2">
                      Aucun fournisseur d'IA n'a pu traiter ce prompt.
                    </p>
                    <p className="text-red-600 text-xs">
                      VÃ©rifiez que les clÃ©s API sont configurÃ©es (OpenAI, Anthropic, Google, ou Perplexity).
                    </p>
                  </div>
                )}
              </div>
            </div>
          </div>
        );
      })}
      
      {/* No results message */}
      {searchQuery && filteredPromptIndices.length === 0 && (
        <div className="text-center py-8 bg-gray-50 rounded-lg">
          <p className="text-gray-600 mb-2">No results found for "{searchQuery}"</p>
          <p className="text-gray-500 text-sm">Try searching for different keywords</p>
        </div>
      )}
    </div>
  );
}

'use client';

/* eslint-disable @next/next/no-img-element */

import React, { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Badge } from '@/components/ui/badge';
import { ProviderSpecificRanking } from '@/lib/types';
import { TrendingUp, TrendingDown, Minus } from 'lucide-react';
import Image from 'next/image';
import { useTranslations } from 'next-intl';

// Provider icon mapping
const getProviderIcon = (provider: string) => {
  switch (provider) {
    case 'OpenAI':
      return (
        <img 
          src="https://cdn.brandfetch.io/idR3duQxYl/theme/dark/symbol.svg?c=1dxbfHSJFAPEGdCLU4o5B" 
          alt="OpenAI" 
          className="w-7 h-7"
        />
      );
    case 'Anthropic':
      return (
        <img 
          src="https://cdn.brandfetch.io/idmJWF3N06/theme/dark/symbol.svg" 
          alt="Anthropic" 
          className="w-6 h-6"
        />
      );
    case 'Google':
      return (
        <div className="w-7 h-7 flex items-center justify-center">
          <svg viewBox="0 0 24 24" className="w-7 h-7">
            <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
            <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
            <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
            <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
          </svg>
        </div>
      );
    case 'Perplexity':
      return (
        <img 
          src="https://cdn.brandfetch.io/idNdawywEZ/w/800/h/800/theme/dark/icon.png?c=1dxbfHSJFAPEGdCLU4o5B" 
          alt="Perplexity" 
          className="w-6 h-6"
        />
      );
    default:
      return <div className="w-7 h-7 bg-gray-400 rounded" />;
  }
};

interface ProviderRankingsTabsProps {
  providerRankings: ProviderSpecificRanking[];
  brandName: string;
  shareOfVoice?: number;
  averagePosition?: number;
  sentimentScore?: number;
  weeklyChange?: number;
}

// Company cell component with favicon support
const CompanyCell = ({ 
  name, 
  isOwn, 
  url 
}: { 
  name: string; 
  isOwn?: boolean; 
  url?: string;
}) => {
  const [faviconError, setFaviconError] = useState(false);
  
  // Generate favicon URL using Google's favicon service
  const faviconUrl = url ? `https://www.google.com/s2/favicons?domain=${url}&sz=64` : null;
  
  return (
    <div className="flex items-center gap-2">
      <div className="w-5 h-5 flex items-center justify-center rounded overflow-hidden flex-shrink-0">
        {faviconUrl && !faviconError ? (
          <Image
            src={faviconUrl}
            alt={`${name} logo`}
            width={20}
            height={20}
            className="object-contain"
            onError={() => setFaviconError(true)}
          />
        ) : (
          <div className="w-5 h-5 bg-gray-200 rounded flex items-center justify-center">
            <span className="text-gray-600 font-semibold text-[10px]">
              {name.charAt(0).toUpperCase()}
            </span>
          </div>
        )}
      </div>
      <span className={`text-sm ${
        isOwn ? 'font-semibold text-black' : 'text-black'
      }`}>
        {name}
      </span>
    </div>
  );
};

// Generate a fallback URL from competitor name
const generateFallbackUrl = (competitorName: string): string | undefined => {
  const cleanName = competitorName.toLowerCase()
    .replace(/[^a-z0-9\s]/g, '')
    .replace(/\s+/g, '')
    .trim();
  
  if (cleanName.length < 3 || ['inc', 'llc', 'corp', 'company', 'the'].includes(cleanName)) {
    return undefined;
  }
  
  return `${cleanName}.com`;
};

export function ProviderRankingsTabs({ 
  providerRankings, 
  brandName,
  shareOfVoice,
  averagePosition,
  sentimentScore,
  weeklyChange
}: ProviderRankingsTabsProps) {
  const t = useTranslations('brandMonitor.providerRankings');
  const [selectedProvider, setSelectedProvider] = useState(
    providerRankings?.[0]?.provider || 'OpenAI'
  );

  if (!providerRankings || providerRankings.length === 0) return null;

  const getSentimentBadge = (sentiment: string) => {
    switch (sentiment) {
      case 'positive':
        return <Badge variant="secondary" className="bg-green-50 text-black text-xs">{t('positive')}</Badge>;
      case 'negative':
        return <Badge variant="secondary" className="bg-red-50 text-black text-xs">{t('negative')}</Badge>;
      default:
        return <Badge variant="secondary" className="bg-gray-50 text-black text-xs">{t('neutral')}</Badge>;
    }
  };

  const getChangeIcon = (change: number | undefined) => {
    if (!change) return <Minus className="h-3 w-3 text-gray-400" />;
    if (change > 0) return <TrendingUp className="h-3 w-3 text-black" />;
    return <TrendingDown className="h-3 w-3 text-black" />;
  };

  // Get the selected provider's data
  const selectedProviderData = providerRankings.find(p => p.provider === selectedProvider);
  const competitors = selectedProviderData?.competitors || [];
  const brandRank = competitors.findIndex(c => c.isOwn) + 1;
  const brandVisibility = competitors.find(c => c.isOwn)?.visibilityScore || 0;

  return (
    <Card className="p-2 bg-card text-card-foreground gap-6 rounded-xl border py-6 shadow-sm border-gray-200 h-full flex flex-col">
      <CardHeader className="border-b">
        <div className="flex justify-between items-center">
          <div>
            <CardTitle className="text-xl font-semibold">{t('title')}</CardTitle>
            <CardDescription className="text-sm text-gray-600 mt-1">
              {t('description')}
            </CardDescription>
          </div>
          <div className="text-right">
            <p className="text-2xl font-bold text-orange-600">#{brandRank}</p>
            <p className="text-xs text-gray-500 mt-1">{t('averageRank')}</p>
          </div>
        </div>
      </CardHeader>
      <CardContent className="pt-6 pb-2 flex-1 flex flex-col">
        <Tabs value={selectedProvider} onValueChange={setSelectedProvider} className="flex-1 flex flex-col">
          <TabsList className={`grid w-full mb-2 h-14 ${providerRankings.length === 2 ? 'grid-cols-2' : providerRankings.length === 3 ? 'grid-cols-3' : 'grid-cols-4'}`}>
            {providerRankings.map(({ provider }) => {
              // Provider info is now handled by icon mapping directly
              return (
                <TabsTrigger 
                  key={provider} 
                  value={provider} 
                  className="text-sm flex items-center justify-center h-full"
                  title={provider}
                >
                  {getProviderIcon(provider)}
                </TabsTrigger>
              );
            })}
          </TabsList>

          {providerRankings.map(({ provider, competitors }) => (
            <TabsContent key={provider} value={provider} className="mt-0">
              <div className="overflow-x-auto rounded-lg border border-gray-200">
                <table className="w-full border-collapse">
                  <thead>
                    <tr>
                      <th className="bg-gray-50 border-b border-r border-gray-200 text-left p-3 text-xs font-medium text-gray-900 w-8">#</th>
                      <th className="bg-gray-50 border-b border-r border-gray-200 text-left p-3 text-xs font-medium text-gray-900 w-[200px]">{t('company')}</th>
                      <th className="bg-gray-50 border-b border-r border-gray-200 text-right p-3 text-xs font-medium text-gray-900">{t('visibility')}</th>
                      <th className="bg-gray-50 border-b border-r border-gray-200 text-right p-3 text-xs font-medium text-gray-900">{t('shareOfVoice')}</th>
                      <th className="bg-gray-50 border-b border-gray-200 text-right p-3 text-xs font-medium text-gray-900">{t('sentiment')}</th>
                    </tr>
                  </thead>
                  <tbody>
                    {competitors.map((competitor, idx) => {
                      const competitorUrl = generateFallbackUrl(competitor.name);
                      
                      return (
                        <tr 
                          key={idx} 
                          className={`
                            ${idx > 0 ? 'border-t border-gray-200' : ''}
                            ${competitor.isOwn 
                              ? 'bg-orange-50' 
                              : 'hover:bg-gray-50 transition-colors'
                            }
                          `}
                        >
                          <td className="border-r border-gray-200 p-3 text-xs text-black">
                            {idx + 1}
                          </td>
                          <td className="border-r border-gray-200 p-3">
                            <CompanyCell 
                              name={competitor.name}
                              isOwn={competitor.isOwn}
                              url={competitorUrl}
                            />
                          </td>
                          <td className="border-r border-gray-200 p-3 text-right">
                            <div className="flex items-center justify-end gap-1">
                              <span className="text-sm font-medium text-black">
                                {competitor.visibilityScore}%
                              </span>
                              {competitor.weeklyChange !== undefined && competitor.weeklyChange !== 0 && (
                                getChangeIcon(competitor.weeklyChange)
                              )}
                            </div>
                          </td>
                          <td className="border-r border-gray-200 p-3 text-right">
                            <span className="text-sm text-black">
                              {competitor.shareOfVoice}%
                            </span>
                          </td>
                          <td className="p-3 text-right">
                            {getSentimentBadge(competitor.sentiment)}
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            </TabsContent>
          ))}
        </Tabs>
        
        {/* Metrics Row at Bottom */}
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-3 mt-6 pt-6 border-t">
          <div className="bg-gray-50 rounded-lg p-4 text-center">
            <p className="text-xs text-gray-500 mb-1">{t('competitors')}</p>
            <p className="text-lg font-semibold text-black">{competitors.length}</p>
          </div>
          <div className="bg-orange-50 rounded-lg p-4 text-center">
            <p className="text-xs text-gray-500 mb-1">{brandName} {t('rank')}</p>
            <p className="text-lg font-semibold text-black">
              #{brandRank}
            </p>
          </div>
          <div className="bg-gray-50 rounded-lg p-4 text-center">
            <p className="text-xs text-gray-500 mb-1">{brandName} {t('visibility')}</p>
            <p className="text-lg font-semibold text-black">
              {brandVisibility}%
            </p>
          </div>
          <div className="bg-gray-50 rounded-lg p-4 text-center">
            <p className="text-xs text-gray-500 mb-1">{t('shareOfVoice')}</p>
            <p className="text-lg font-semibold text-black">{shareOfVoice}%</p>
          </div>
          <div className="bg-gray-50 rounded-lg p-4 text-center">
            <p className="text-xs text-gray-500 mb-1">{t('averagePosition')}</p>
            <p className="text-lg font-semibold text-black">#{averagePosition}</p>
          </div>
          <div className="bg-gray-50 rounded-lg p-4 text-center">
            <p className="text-xs text-gray-500 mb-1">{t('sentimentScore')}</p>
            <p className="text-lg font-semibold text-black">{sentimentScore}%</p>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { PieChart, Pie, Cell, Tooltip, ResponsiveContainer } from 'recharts';
import { CompetitorRanking } from '@/lib/types';
import { IdentifiedCompetitor } from '@/lib/brand-monitor-reducer';
import { useTranslations } from 'next-intl';

interface VisibilityScoreTabProps {
  competitors: CompetitorRanking[];
  brandData: CompetitorRanking;
  identifiedCompetitors: IdentifiedCompetitor[];
}

export function VisibilityScoreTab({
  competitors,
  brandData,
  identifiedCompetitors
}: VisibilityScoreTabProps) {
  const t = useTranslations('brandMonitor.visibilityScore');
  const topCompetitor = competitors.filter(c => !c.isOwn)[0];
  const brandRank = competitors.findIndex(c => c.isOwn) + 1;
  const difference = topCompetitor ? brandData.visibilityScore - topCompetitor.visibilityScore : 0;
  
  return (
    <div className="flex flex-col h-full">
      {/* Main Content Card */}
      <Card className="p-2 bg-card text-card-foreground gap-6 rounded-xl border py-6 shadow-sm border-gray-200 h-full flex flex-col">
        <CardHeader className="border-b">
          <div className="flex justify-between items-center">
            <div>
              <CardTitle className="text-xl font-semibold">{t('title')}</CardTitle>
              <CardDescription className="text-sm text-gray-600 mt-1">
                {t('description')}
              </CardDescription>
            </div>
            {/* Visibility Score in top right */}
            <div className="text-right">
              <p className="text-3xl font-bold text-orange-600">{brandData.visibilityScore}%</p>
              <p className="text-xs text-gray-500 mt-1">{t('overallScore')}</p>
            </div>
          </div>
        </CardHeader>
        <CardContent className="pt-6 flex-1">
          <div className="flex gap-8">
            {/* Left side - Pie Chart */}
            <div className="flex-1">
              <div className="h-80 relative">
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <defs>
                      <linearGradient id="orangeGradient" x1="0" y1="0" x2="1" y2="1">
                        <stop offset="0%" stopColor="#fb923c" />
                        <stop offset="100%" stopColor="#ea580c" />
                      </linearGradient>
                      <filter id="shadow" x="-50%" y="-50%" width="200%" height="200%">
                        <feGaussianBlur in="SourceAlpha" stdDeviation="3"/>
                        <feOffset dx="0" dy="2" result="offsetblur"/>
                        <feFlood floodColor="#000000" floodOpacity="0.1"/>
                        <feComposite in2="offsetblur" operator="in"/>
                        <feMerge>
                          <feMergeNode/>
                          <feMergeNode in="SourceGraphic"/>
                        </feMerge>
                      </filter>
                    </defs>
                    <Pie
                      data={competitors.slice(0, 8).map((competitor) => ({
                        name: competitor.name,
                        value: competitor.visibilityScore,
                        isOwn: competitor.isOwn
                      }))}
                      cx="50%"
                      cy="50%"
                      innerRadius={70}
                      outerRadius={110}
                      paddingAngle={1}
                      dataKey="value"
                      startAngle={90}
                      endAngle={-270}
                      animationBegin={0}
                      animationDuration={800}
                      filter="url(#shadow)"
                    >
                      {competitors.slice(0, 8).map((competitor, idx) => (
                        <Cell 
                          key={`cell-${idx}`} 
                          fill={competitor.isOwn ? 'url(#orangeGradient)' : 
                            ['#3b82f6', '#8b5cf6', '#ec4899', '#10b981', '#f59e0b', '#6366f1', '#14b8a6', '#f43f5e'][idx % 8]}
                          stroke={competitor.isOwn ? '#ea580c' : 'none'}
                          strokeWidth={competitor.isOwn ? 2 : 0}
                        />
                      ))}
                    </Pie>
                    <Tooltip 
                      contentStyle={{ 
                        backgroundColor: 'rgba(255, 255, 255, 0.95)', 
                        border: '1px solid #e5e7eb',
                        borderRadius: '8px',
                        fontSize: '14px',
                        padding: '8px 12px',
                        boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                      }}
                      formatter={(value: number, name: string) => [`${value}% ${t('visibilityTooltip')}`, name]}
                      labelStyle={{ fontWeight: 600 }}
                    />
                  </PieChart>
                </ResponsiveContainer>
                
                {/* Center text showing relative performance */}
                <div className="absolute top-[50%] left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-center">
                  <p className="text-3xl font-bold text-gray-900">#{brandRank}</p>
                  <p className="text-sm text-gray-500 mt-1">{t('rank')}</p>
                  {difference !== 0 && (
                    <p className={`text-xs mt-2 font-medium ${difference > 0 ? 'text-green-600' : 'text-red-600'}`}>
                      {difference > 0 ? '+' : ''}{Math.abs(difference).toFixed(1)}% vs #1
                    </p>
                  )}
                </div>
              </div>
            </div>
            
            {/* Right side - Legend */}
            <div className="w-80 space-y-2">
              {competitors.slice(0, 8).map((competitor, idx) => {
                const competitorData = identifiedCompetitors.find(c => 
                  c.name === competitor.name || 
                  c.name.toLowerCase() === competitor.name.toLowerCase()
                );
                const faviconUrl = competitorData?.url ? 
                  `https://www.google.com/s2/favicons?domain=${competitorData.url}&sz=64` : null;
                
                const color = competitor.isOwn ? '#ea580c' : 
                  ['#3b82f6', '#8b5cf6', '#ec4899', '#10b981', '#f59e0b', '#6366f1', '#14b8a6', '#f43f5e'][idx % 8];
                
                return (
                  <div key={idx} className="flex items-center gap-2">
                    <div 
                      className="w-3 h-3 rounded-full flex-shrink-0" 
                      style={{ backgroundColor: color }}
                    />
                    <div className="flex items-center gap-2 min-w-0">
                      <div className="w-5 h-5 rounded flex items-center justify-center bg-gray-100 flex-shrink-0">
                        {faviconUrl ? (
                          <img 
                            src={faviconUrl}
                            alt={competitor.name}
                            className="w-4 h-4 object-contain"
                            onError={(e) => {
                              e.currentTarget.style.display = 'none';
                              const fallback = e.currentTarget.nextSibling as HTMLDivElement;
                              if (fallback) fallback.style.display = 'flex';
                            }}
                          />
                        ) : null}
                        <div className={`w-full h-full ${
                          competitor.isOwn ? 'bg-orange-500' : 'bg-gray-300'
                        } flex items-center justify-center text-white text-[8px] font-bold rounded`} 
                        style={{ display: faviconUrl ? 'none' : 'flex' }}>
                          {competitor.name.charAt(0)}
                        </div>
                      </div>
                      <span className={`text-sm truncate ${
                        competitor.isOwn ? 'font-semibold text-orange-600' : 'text-gray-700'
                      }`}>
                        {competitor.name}
                      </span>
                      <span className="text-sm font-medium text-gray-900 ml-auto">
                        {competitor.visibilityScore}%
                      </span>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

