          // Mise Ã  jour de la progression (70-90% de la progression totale)
          processedResponses++;
          const extractionProgress = (processedResponses / totalResponses) * 20; // 20% pour l'extraction (70% Ã  90%)
          const progress = Math.round(70 + extractionProgress);

          if (sendEvent) {
            await sendEvent({
              type: 'brand-extraction-progress',
              stage: 'extracting-brands',
              data: {
                stage: 'extracting-brands',
                provider,
                responseIndex: index + 1,
                totalResponses: providerResponses.length,
                progress,
                message: `Extracting brands from ${provider} response ${index + 1}/${providerResponses.length}`
              },
              timestamp: new Date()
            });
          }

          return extraction;
        } catch (error) {
          console.error(`[ProviderComparison] âŒ Erreur extraction ${provider}-${index + 1}:`, error);
          processedResponses++;

          // Mise Ã  jour de la progression mÃªme en cas d'erreur
          const extractionProgress = (processedResponses / totalResponses) * 20;
          const progress = Math.round(70 + extractionProgress);

          if (sendEvent) {
            await sendEvent({
              type: 'brand-extraction-progress',
              stage: 'extracting-brands',
              data: {
                stage: 'extracting-brands',
                provider,
                responseIndex: index + 1,
                totalResponses: providerResponses.length,
                progress,
                message: `Error extracting brands from ${provider} response ${index + 1}/${providerResponses.length}`
              },
              timestamp: new Date()
            });
          }

          return null;
        }
      });

    const providerExtractions = await Promise.all(responseExtractionPromises);
    return { provider, extractions: providerExtractions.filter(e => e !== null) };
  });

  // Attendre toutes les extractions
  const providerResults = await Promise.all(providerExtractionPromises);

  // Stocker les rÃ©sultats
  providerResults.forEach(({ provider, extractions }) => {
    brandExtractions.set(provider, extractions);
  });

  // 4. Ã‰TAPE 5: Calculer les dÃ©tections par provider
  console.log(`[ProviderComparison] ðŸ“Š Calcul des dÃ©tections par provider...`);
  const providerDetections = calculateBrandVisibilityByProvider(brandExtractions, company.name, knownCompetitors);

  // 5. Ã‰TAPE 6: Construire les rÃ©sultats dans le format attendu
  const providers = Array.from(responsesByProvider.keys());
  const providerRankings: ProviderSpecificRanking[] = [];
  const providerComparison: ProviderComparisonData[] = [];

  // CrÃ©er les rankings par provider basÃ©s sur la nouvelle dÃ©tection
  providers.forEach(provider => {
    const competitors: CompetitorRanking[] = [];
    const providerResponses = responsesByProvider.get(provider)!;
    const totalResponses = providerResponses.length;
    const providerBrandDetections = providerDetections.get(provider);

    console.log(`[ProviderComparison] ðŸ” Construction des rÃ©sultats pour ${provider}: ${totalResponses} rÃ©ponses`);

    allBrands.forEach(brandName => {
      const detection = providerBrandDetections?.get(brandName);
      const mentioned = detection?.mentioned || false;
      const mentionCount = detection?.mentionCount || 0;
      const totalResponses = detection?.totalResponses || providerResponses.length;
      const visibilityScore = detection?.percentage || 0; // Utiliser le pourcentage rÃ©el

      // Pour les positions et sentiments, on garde la logique existante des rankings
      const brandRankings = providerResponses
        .flatMap(r => r.rankings || [])
        .filter(r => r.company === brandName);

      const positions = brandRankings
        .map(r => r.position)
        .filter(p => p !== null && p !== undefined) as number[];

      const sentiments = brandRankings
        .map(r => r.sentiment)
        .filter(s => s !== null && s !== undefined) as ('positive' | 'neutral' | 'negative')[];

      const avgPosition = positions.length > 0
        ? positions.reduce((a, b) => a + b, 0) / positions.length
        : 99;

      competitors.push({
        name: brandName,
        mentions: mentionCount, // Utiliser le nombre rÃ©el de mentions
        averagePosition: Math.round(avgPosition * 10) / 10,
        sentiment: determineSentiment(sentiments),
        sentimentScore: calculateSentimentScore(sentiments),
        shareOfVoice: 0, // Will calculate after
        visibilityScore: Math.round(visibilityScore * 10) / 10,
        isOwn: brandName === company.name,
      });

      console.log(`[ProviderComparison] ðŸ“Š ${brandName} (${provider}): ${mentionCount}/${totalResponses} = ${visibilityScore}%`);
    });

    // Calculate share of voice for this provider
    const totalMentions = competitors.reduce((sum, c) => sum + c.mentions, 0);
    competitors.forEach(c => {
      c.shareOfVoice = totalMentions > 0 
        ? Math.round((c.mentions / totalMentions) * 1000) / 10 
        : 0;
    });

    // Sort by visibility score
    competitors.sort((a, b) => b.visibilityScore - a.visibilityScore);

    providerRankings.push({
      provider,
      competitors,
    });
  });

  // CrÃ©er la matrice de comparaison avec la nouvelle dÃ©tection
  allBrands.forEach(brandName => {
    const comparisonData: ProviderComparisonData = {
      competitor: brandName,
      providers: {},
      isOwn: brandName === company.name,
    };

    let hasAnyMentions = false;

    providerRankings.forEach(({ provider, competitors }) => {
      const competitor = competitors.find(c => c.name === brandName);
      if (competitor) {
        comparisonData.providers[provider] = {
          visibilityScore: competitor.visibilityScore,
          position: competitor.averagePosition,
          mentions: competitor.mentions,
          sentiment: competitor.sentiment,
        };

        if (competitor.mentions > 0) {
          hasAnyMentions = true;
        }
      }
    });

    console.log(`[ProviderComparison] ðŸ” Analyse ${brandName}:`);
    console.log(`  - hasAnyMentions: ${hasAnyMentions}`);
    console.log(`  - isOwn: ${brandName === company.name}`);
    console.log(`  - providers avec donnÃ©es: ${Object.keys(comparisonData.providers).length}`);

    // Inclure TOUTES les marques sÃ©lectionnÃ©es au dÃ©part (knownCompetitors + company)
    // mÃªme si elles ont 0% de score - c'est important pour la cohÃ©rence de l'UI
    const isSelectedBrand = brandName === company.name || knownCompetitors.includes(brandName);

    if (isSelectedBrand) {
      providerComparison.push(comparisonData);
      console.log(`[ProviderComparison] âœ… Including ${brandName} in matrix (selected brand, score: ${hasAnyMentions ? 'with mentions' : '0%'})`);
    } else {
      console.log(`[ProviderComparison] âŒ Excluding ${brandName} from matrix (not in selected brands)`);
    }
  });

  // Trier par score de visibilitÃ© moyen
  providerComparison.sort((a, b) => {
    const avgA = Object.values(a.providers).reduce((sum, p) => sum + p.visibilityScore, 0) / Object.keys(a.providers).length;
    const avgB = Object.values(b.providers).reduce((sum, p) => sum + p.visibilityScore, 0) / Object.keys(b.providers).length;
    return avgB - avgA;
  });

  return { providerRankings, providerComparison };
}

// Mock response generator for demo mode
function generateMockResponse(
  prompt: string,
  provider: string,
  brandName: string,
  competitors: string[]
): AIResponse {
  // Simulate some delay
  const delay = Math.random() * 500 + 200;

  // Create a realistic-looking ranking
  const allCompanies = [brandName, ...competitors].slice(0, 10);
  const shuffled = [...allCompanies].sort(() => Math.random() - 0.5);

  const rankings: CompanyRanking[] = shuffled.slice(0, 5).map((company, index) => ({
    position: index + 1,
    company,
    reason: `${company} offers strong features in this category`,
    sentiment: Math.random() > 0.7 ? 'positive' : Math.random() > 0.3 ? 'neutral' : 'negative' as const,
  }));

  const brandRanking = rankings.find(r => r.company === brandName);
  const brandMentioned = !!brandRanking || Math.random() > 0.3;
  const brandPosition = brandRanking?.position || (brandMentioned ? Math.floor(Math.random() * 8) + 3 : undefined);

  // Get the proper display name for the provider
  const providerDisplayName = provider === 'openai' ? 'OpenAI' :
                             provider === 'anthropic' ? 'Anthropic' :
                             provider === 'google' ? 'Google' :
                             provider === 'perplexity' ? 'Perplexity' :
                             provider; // fallback to original

  return {
    provider: providerDisplayName,
    prompt,
    response: `Based on my analysis, here are the top solutions:\n\n${rankings.map(r => 
      `${r.position}. ${r.company} - ${r.reason}`
    ).join('\n')}\n\nThese rankings are based on features, user satisfaction, and market presence.`,
    rankings,
    competitors: competitors.filter(() => Math.random() > 0.5),
    brandMentioned,
    brandPosition,
    sentiment: brandRanking?.sentiment || 'neutral',
    confidence: Math.random() * 0.3 + 0.7,
    timestamp: new Date(),
  };
}

import { AIResponse, AnalysisProgressData, Company, PartialResultData, ProgressData, PromptGeneratedData, ScoringProgressData, SSEEvent, AnalysisSource } from './types';
import { generatePromptsForCompany, analyzePromptWithProvider, calculateBrandScores, analyzeCompetitors, identifyCompetitors, analyzeCompetitorsByProvider } from './ai-utils';
import { analyzePromptWithProvider as analyzePromptWithProviderEnhanced } from './ai-utils-enhanced';
import { canonicalizeBrandsWithOpenAI } from './openai-web-search';
import { extractAnalysisSources } from './brand-monitor-sources';
import { getConfiguredProviders } from './provider-config';
import { apiUsageTracker } from './api-usage-tracker';
import { logger } from './logger';

export interface AnalysisConfig {
  company: Company;
  customPrompts?: string[];
  userSelectedCompetitors?: { name: string }[];
  useWebSearch?: boolean;
  sendEvent: (event: SSEEvent) => Promise<void>;
  locale?: string;
}

export interface AnalysisResult {
  company: Company;
  knownCompetitors: string[];
  prompts: any[];
  responses: AIResponse[];
  scores: any;
  competitors: any[];
  providerRankings: any;
  providerComparison: any;
  sources: AnalysisSource[];
  errors?: string[];
  webSearchUsed?: boolean;
  apiUsageSummary?: any;
}

/**
 * Common analysis logic extracted from both API routes
 */
export async function performAnalysis({
  company,
  customPrompts,
  userSelectedCompetitors,
  useWebSearch = false,
  sendEvent,
  locale
}: AnalysisConfig): Promise<AnalysisResult> {
  // Send start event
  await sendEvent({
    type: 'start',
    stage: 'initializing',
    data: { 
      message: `Starting analysis for ${company.name}${useWebSearch ? ' with web search' : ''}` 
    } as ProgressData,
    timestamp: new Date()
  });

  // Stage 1: Identify competitors
  await sendEvent({
    type: 'stage',
    stage: 'identifying-competitors',
    data: { 
      stage: 'identifying-competitors',
      progress: 0,
      message: 'Identifying competitors...'
    } as ProgressData,
    timestamp: new Date()
  });

  // Use user-selected competitors if provided, otherwise identify them
  let competitors: string[];
  if (userSelectedCompetitors && userSelectedCompetitors.length > 0) {
    competitors = userSelectedCompetitors.map(c => c.name);
    logger.info('Using user-selected competitors:', competitors);
    
    // Send competitor events for UI
    for (let i = 0; i < competitors.length; i++) {
      await sendEvent({
        type: 'competitor-found',
        stage: 'identifying-competitors',
        data: { 
          competitor: competitors[i],
          index: i + 1,
          total: competitors.length
        },
        timestamp: new Date()
      });
    }
  } else {
    competitors = await identifyCompetitors(company, sendEvent);
  }

  // Canonicalize competitors once (single OpenAI call) and propagate
  try {
    const { canonicalNames, mapping } = await canonicalizeBrandsWithOpenAI(competitors, locale);
    logger.debug('[Canonicalizer] Raw -> Canonical mapping:', mapping);
    logger.debug('[Canonicalizer] Canonical competitors:', canonicalNames);
    competitors = canonicalNames;
  } catch (e) {
    logger.warn('[Canonicalizer] Failed to canonicalize competitors, using raw list:', (e as Error)?.message);
  }

  // Stage 2: Generate prompts
  // Skip the 100% progress for competitors and go straight to the next stage
  await sendEvent({
    type: 'stage',
    stage: 'generating-prompts',
    data: {
      stage: 'generating-prompts',
      progress: 0,
      message: 'Generating analysis prompts...'
    } as ProgressData,
    timestamp: new Date()
  });

  // Use custom prompts if provided, otherwise generate them
  let analysisPrompts;
  if (customPrompts && customPrompts.length > 0) {
    // Convert string prompts to BrandPrompt objects
    analysisPrompts = customPrompts.map((prompt: string, index: number) => ({
      id: `custom-${index}`,
      prompt,
      category: 'custom' as const
    }));
  } else {
    const prompts = await generatePromptsForCompany(company, competitors);
    // Note: Changed from 8 to 4 to match UI - this should be configurable
    analysisPrompts = prompts.slice(0, 4);
  }

  // Send prompt generated events
  for (let i = 0; i < analysisPrompts.length; i++) {
    await sendEvent({
      type: 'prompt-generated',
      stage: 'generating-prompts',
      data: {
        prompt: analysisPrompts[i].prompt,
        category: analysisPrompts[i].category,
        index: i + 1,
        total: analysisPrompts.length
      } as PromptGeneratedData,
      timestamp: new Date()
    });
  }

  // Stage 3: Analyze with AI providers (0-70% of total progress)
  await sendEvent({
    type: 'stage',
    stage: 'analyzing-prompts',
    data: {
      stage: 'analyzing-prompts',
      progress: 0,
      message: `Starting AI analysis${useWebSearch ? ' with web search' : ''}...`
    } as ProgressData,
    timestamp: new Date()
  });

  const responses: AIResponse[] = [];
  const errors: string[] = [];
  
  // Filter providers based on available API keys
  const availableProviders = getAvailableProviders();
  
  logger.debug('=== PROVIDER DIAGNOSIS ===');
  logger.debug('Available providers for analysis:', availableProviders.map(p => p.name));
  logger.debug('Available provider details:', availableProviders.map(p => ({ name: p.name, model: p.model })));
  logger.debug('Environment variables:', {
    hasOpenAI: !!process.env.OPENAI_API_KEY,
    hasAnthropic: !!process.env.ANTHROPIC_API_KEY,
    hasGoogle: !!process.env.GOOGLE_GENERATIVE_AI_API_KEY,
    hasPerplexity: !!process.env.PERPLEXITY_API_KEY
  });
  logger.debug('Raw configured providers:', getConfiguredProviders().map(p => ({ 
    name: p.name, 
    enabled: p.enabled, 
    configured: p.isConfigured() 
  })));
  logger.info('Web search enabled:', useWebSearch);
  logger.info('Number of prompts:', analysisPrompts.length);
  logger.info('Number of available providers:', availableProviders.length);
  
  let completedAnalyses = 0;

  // Check if we should use mock mode (no API keys configured)
  const useMockMode = process.env.USE_MOCK_MODE === 'true' || availableProviders.length === 0;

  // If no providers are available and we're not in mock mode, add mock providers
  let workingProviders = availableProviders;
  if (availableProviders.length === 0) {
    logger.warn('No providers configured, using mock providers for demonstration');
    workingProviders = [
      { name: 'OpenAI', model: 'gpt-4', icon: 'ðŸ¤–' },
      { name: 'Anthropic', model: 'claude-3', icon: 'ðŸ”®' },
      { name: 'Google', model: 'gemini-pro', icon: 'ðŸŒŸ' }
    ];
  }

  // If still no providers available, return early with error
  if (workingProviders.length === 0) {
    logger.error('No providers available for analysis');
    await sendEvent({
      type: 'error',
      stage: 'analyzing-prompts',
      data: {
        message: 'Aucun fournisseur d\'IA configurÃ©. Veuillez configurer au moins une clÃ© API (OpenAI, Anthropic, Google, ou Perplexity).'
      },
      timestamp: new Date()
    });
    return {
      company,
      knownCompetitors: [],
      prompts: analysisPrompts,
      responses: [],
      scores: {
        visibilityScore: 0,
        sentimentScore: 0,
        shareOfVoice: 0,
        overallScore: 0,
        averagePosition: 0
      },
      competitors: [],
      providerRankings: [],
      providerComparison: [],
      errors: ['No AI providers configured'],
      webSearchUsed: useWebSearch,
    };
  }

  // Recalculate total analyses with working providers
  const totalAnalyses = analysisPrompts.length * workingProviders.length;
  logger.info('Updated total analyses to perform:', totalAnalyses);

  // Process prompts in parallel batches of 3
  const BATCH_SIZE = 3;
  
  for (let batchStart = 0; batchStart < analysisPrompts.length; batchStart += BATCH_SIZE) {
    const batchEnd = Math.min(batchStart + BATCH_SIZE, analysisPrompts.length);
    const batchPrompts = analysisPrompts.slice(batchStart, batchEnd);
    
    // Create all analysis promises for this batch
    const batchPromises = batchPrompts.flatMap((prompt, batchIndex) => 
      workingProviders.map(async (provider) => {
        const promptIndex = batchStart + batchIndex;
        
        // Send analysis start event
        await sendEvent({
          type: 'analysis-start',
          stage: 'analyzing-prompts',
          data: {
            provider: provider.name,
            prompt: prompt.prompt,
            promptIndex: promptIndex + 1,
            totalPrompts: analysisPrompts.length,
            providerIndex: 0,
            totalProviders: workingProviders.length,
            status: 'started'
          } as AnalysisProgressData,
          timestamp: new Date()
        });

        try {
          // Debug log for each provider attempt
          logger.debug(`\n=== STARTING ANALYSIS ===`);
          logger.debug(`Provider: ${provider.name}`);
          logger.debug(`Prompt: "${prompt.prompt.substring(0, 50)}..."`); 
          logger.debug(`Use mock mode: ${useMockMode}`);
          logger.debug(`Use web search: ${useWebSearch}`);
          logger.debug(`Brand: ${company.name}`);
          logger.debug(`Competitors: ${competitors.slice(0, 3).join(', ')}${competitors.length > 3 ? '...' : ''}`);
          
          // Call the appropriate analysis function based on useWebSearch
          const response = useWebSearch 
            ? await analyzePromptWithProviderEnhanced(
                prompt.prompt, 
                provider.name, 
                company.name, 
                competitors,
                useMockMode,
                true, // useWebSearch parameter for enhanced version
                locale
              )
            : await analyzePromptWithProvider(
                prompt.prompt, 
                provider.name, 
                company.name, 
                competitors,
                useMockMode,
                locale
              );
          
          logger.debug(`\n=== ANALYSIS COMPLETED ===`);
          logger.debug(`Provider: ${provider.name}`);
          logger.debug(`Has response: ${!!response}`);
          if (response) {
            logger.debug(`Response provider: ${response.provider}`);
            logger.debug(`Brand mentioned: ${response.brandMentioned}`);
            logger.debug(`Response length: ${response.response?.length || 0}`);
            logger.debug(`Response preview: "${response.response?.substring(0, 100) || 'NO RESPONSE'}"`); 
            logger.debug(`Competitors found: ${response.competitors?.length || 0}`);
          } else {
            logger.debug(`Response is null - provider likely not configured`);
          }
          
          // Skip if provider returned null (not configured)
          if (response === null) {
            logger.debug(`Skipping ${provider.name} - not configured`);
            
            // Send analysis complete event with skipped status
            await sendEvent({
              type: 'analysis-complete',
              stage: 'analyzing-prompts',
              data: {
                provider: provider.name,
                prompt: prompt.prompt,
                promptIndex: promptIndex + 1,
                totalPrompts: analysisPrompts.length,
                providerIndex: 0,
                totalProviders: workingProviders.length,
                status: 'failed'
              } as AnalysisProgressData,
              timestamp: new Date()
            });
            
            // Continue with next provider instead of returning early
            completedAnalyses++;
            const progress = Math.round((completedAnalyses / totalAnalyses) * 100);
            
            await sendEvent({
              type: 'progress',
              stage: 'analyzing-prompts',
              data: {
                stage: 'analyzing-prompts',
                progress,
                message: `Completed ${completedAnalyses} of ${totalAnalyses} analyses`
              } as ProgressData,
              timestamp: new Date()
            });
            
            return; // Return early to continue with next provider
          }
          
          // If using mock mode, add a small delay for visual effect
          if (useMockMode) {
            await new Promise(resolve => setTimeout(resolve, Math.random() * 1000 + 500));
          }
          
          responses.push(response);
          logger.debug(`[AnalyzeCommon] âœ… Response added to collection. Total responses: ${responses.length}`);
          logger.debug(`[AnalyzeCommon] Response details:`, {
            provider: response.provider,
            promptPreview: response.prompt.substring(0, 50) + '...',
            responseLength: response.response.length,
            brandMentioned: response.brandMentioned
          });

          // Send partial result
          await sendEvent({
            type: 'partial-result',
            stage: 'analyzing-prompts',
            data: {
              provider: provider.name,
              prompt: prompt.prompt,
              response: {
                provider: response.provider,
                brandMentioned: response.brandMentioned,
                brandPosition: response.brandPosition,
                sentiment: response.sentiment
              }
            } as PartialResultData,
            timestamp: new Date()
          });

          // Send analysis complete event
          await sendEvent({
            type: 'analysis-complete',
            stage: 'analyzing-prompts',
            data: {
              provider: provider.name,
              prompt: prompt.prompt,
              promptIndex: promptIndex + 1,
              totalPrompts: analysisPrompts.length,
              providerIndex: 0,
              totalProviders: workingProviders.length,
              status: 'completed'
            } as AnalysisProgressData,
            timestamp: new Date()
          });

        } catch (error) {
          logger.error(`Error with ${provider.name} for prompt "${prompt.prompt}":`, error);
          
          // Check if it's an authentication error
          const isAuthError = error instanceof Error && (
            error.message.includes('401') || 
            error.message.includes('invalid') || 
            error.message.includes('Authorization Required') ||
            error.message.includes('authentication_error')
          );
          
          if (isAuthError) {
            logger.warn(`Skipping ${provider.name} - authentication error (API key issue)`);
            errors.push(`${provider.name}: API key not configured or invalid`);
          } else {
            errors.push(`${provider.name}: ${error instanceof Error ? error.message : 'Unknown error'}`);
          }
          
          // Send analysis failed event
          await sendEvent({
            type: 'analysis-complete',
            stage: 'analyzing-prompts',
            data: {
              provider: provider.name,
              prompt: prompt.prompt,
              promptIndex: promptIndex + 1,
              totalPrompts: analysisPrompts.length,
              providerIndex: 0,
              totalProviders: workingProviders.length,
              status: 'failed'
            } as AnalysisProgressData,
            timestamp: new Date()
          });
        }

        completedAnalyses++;
        const progress = Math.round((completedAnalyses / totalAnalyses) * 100);
        
        await sendEvent({
          type: 'progress',
          stage: 'analyzing-prompts',
          data: {
            stage: 'analyzing-prompts',
            progress,
            message: `Completed ${completedAnalyses} of ${totalAnalyses} analyses`
          } as ProgressData,
          timestamp: new Date()
        });
      })
    );
    
    // Wait for all promises in this batch to complete
    await Promise.all(batchPromises);
  }

  // Stage 4: Extract brands from responses (70-90% of total progress)
  await sendEvent({
    type: 'stage',
    stage: 'extracting-brands',
    data: {
      stage: 'extracting-brands',
      progress: 70,
      message: 'Extracting brand mentions from AI responses...'
    } as ProgressData,
    timestamp: new Date()
  });

  // Analyze competitors by provider with progress tracking
  const { providerRankings, providerComparison } = await analyzeCompetitorsByProvider(
    company, 
    responses, 
    competitors,
    sendEvent
  );

  await sendEvent({
    type: 'progress',
    stage: 'extracting-brands',
    data: {
      stage: 'extracting-brands',
      progress: 90,
      message: 'Brand extraction complete'
    } as ProgressData,
    timestamp: new Date()
  });

  // Stage 5: Calculate scores (90-100% of total progress)
  await sendEvent({
    type: 'stage',
    stage: 'calculating-scores',
    data: {
      stage: 'calculating-scores',
      progress: 90,
      message: 'Calculating brand visibility scores...'
    } as ProgressData,
    timestamp: new Date()
  });

  // Analyze competitors from all responses
  let competitorRankings = await analyzeCompetitors(company, responses, competitors);

  // Harmonize with provider-level detections: if a brand has 0 mentions across all providers,
  // force its visibility score to 0% to avoid discrepancies (e.g., 0 mentions showing ~6%).
  try {
    const zeroMentionBrands = new Set<string>();
    providerComparison.forEach((row: any) => {
      const providersData = row?.providers || {};
      const totalMentions = Object.values(providersData).reduce((sum: number, p: any) => sum + (p?.mentions || 0), 0);
      if (totalMentions === 0) {
        zeroMentionBrands.add(row.competitor);
      }
    });

    if (zeroMentionBrands.size > 0) {
      competitorRankings = competitorRankings.map(r =>
        zeroMentionBrands.has(r.name)
          ? { ...r, mentions: 0, visibilityScore: 0 }
          : r
      );
    }
  } catch (e) {
    logger.warn('[AnalyzeCommon] Failed to harmonize zero-mention brands:', (e as Error)?.message);
  }

  // Recompute global visibility as average of provider scores for consistency
  try {
    competitorRankings = competitorRankings.map(r => {
      // Find this brand in providerComparison
      const providerRow = providerComparison.find(row => row.competitor === r.name);
      if (!providerRow || !providerRow.providers) return r;

      // Calculate average visibility score across all providers
      const providerScores = Object.values(providerRow.providers)
        .map((p: any) => p?.visibilityScore || 0)
        .filter(score => typeof score === 'number' && !isNaN(score));
      
      if (providerScores.length === 0) return r;

      const averageVisibility = providerScores.reduce((sum, score) => sum + score, 0) / providerScores.length;
      
      // Calculate total mentions across all providers for share of voice
      const totalMentions = Object.values(providerRow.providers)
        .reduce((sum: number, p: any) => sum + (p?.mentions || 0), 0);

      return {
        ...r,
        mentions: totalMentions,
        visibilityScore: Math.round(averageVisibility * 10) / 10,
      };
    });

    // Recompute share of voice with the adjusted mentions
    const totalMentionsAdjusted = competitorRankings.reduce((sum, c) => sum + c.mentions, 0);
    competitorRankings = competitorRankings.map(c => ({
      ...c,
      shareOfVoice: totalMentionsAdjusted > 0 ? Math.round((c.mentions / totalMentionsAdjusted) * 1000) / 10 : 0,
    }));

    // Sort after adjustments
    competitorRankings.sort((a, b) => b.visibilityScore - a.visibilityScore);
  } catch (e) {
    logger.warn('[AnalyzeCommon] Failed to recompute global visibility from providerComparison:', (e as Error)?.message);
  }

  // Send scoring progress for each competitor
  for (let i = 0; i < competitorRankings.length; i++) {
    await sendEvent({
      type: 'scoring-start',
      stage: 'calculating-scores',
      data: {
        competitor: competitorRankings[i].name,
        score: competitorRankings[i].visibilityScore,
        index: i + 1,
        total: competitorRankings.length
      } as ScoringProgressData,
      timestamp: new Date()
    });
  }

  // Calculate final scores
  const scores = calculateBrandScores(responses, company.name, competitorRankings);

  await sendEvent({
    type: 'progress',
    stage: 'calculating-scores',
    data: {
      stage: 'calculating-scores',
      progress: 100,
      message: 'Scoring complete'
    } as ProgressData,
    timestamp: new Date()
  });

  // Stage 6: Finalize
  await sendEvent({
    type: 'stage',
    stage: 'finalizing',
    data: {
      stage: 'finalizing',
      progress: 100,
      message: 'Analysis complete!'
    } as ProgressData,
    timestamp: new Date()
  });

  logger.info(`[AnalyzeCommon] ðŸŽ¯ Final analysis result:`, {
    totalResponses: responses.length,
    totalPrompts: analysisPrompts.length,
    responsesPerPrompt: responses.length / Math.max(analysisPrompts.length, 1),
    webSearchUsed: useWebSearch,
    errors: errors.length
  });
  
  if (responses.length > 0) {
    logger.debug(`[AnalyzeCommon] âœ… Responses summary:`, 
      responses.map(r => ({
        provider: r.provider,
        promptPreview: r.prompt.substring(0, 30) + '...',
        brandMentioned: r.brandMentioned
      }))
    );
  } else {
    logger.error(`[AnalyzeCommon] âŒ No responses collected!`);
  }

  const analysisResult: AnalysisResult = {
    company,
    knownCompetitors: competitors,
    prompts: analysisPrompts,
    responses,
    scores,
    competitors: competitorRankings,
    providerRankings,
    providerComparison,
    sources: [],
    errors: errors.length > 0 ? errors : undefined,
    webSearchUsed: useWebSearch,
    apiUsageSummary: apiUsageTracker.getSummary(),
  };

  analysisResult.sources = extractAnalysisSources(analysisResult);

  return analysisResult;
}

/**
 * Get available providers based on configured API keys
 */
export function getAvailableProviders() {
  const configuredProviders = getConfiguredProviders();
  // Map to the format expected by the rest of the code
  return configuredProviders.map(provider => ({
    name: provider.name,
    model: provider.defaultModel,
    icon: provider.icon,
  }));
}

/**
 * Create SSE message with proper format
 */
export function createSSEMessage(event: SSEEvent): string {
  // Ensure proper SSE format with event type
  const lines: string[] = [];
  if (event.type) {
    lines.push(`event: ${event.type}`);
  }
  lines.push(`data: ${JSON.stringify(event)}`);
  lines.push(''); // Empty line to signal end of event
  lines.push(''); // Extra newline for proper SSE format
  return lines.join('\n');
}

import Anthropic from '@anthropic-ai/sdk';
import { AIResponse } from './types';
import { apiUsageTracker, extractTokensFromUsage, estimateCost } from './api-usage-tracker';

interface WebSearchResult {
  url: string;
  title: string;
  cited_text?: string;
}

interface AnalysisResult {
  response: string;
  brandMentioned: boolean;
  brandPosition?: number;
  sentiment: 'positive' | 'neutral' | 'negative';
  confidence: number;
  webSearchResults?: WebSearchResult[];
}

/**
 * Analyze brand visibility using Anthropic's native web search capability
 */
export async function analyzeWithAnthropicWebSearch(
  prompt: string,
  brandName: string,
  competitors: string[],
  anthropicApiKey: string
): Promise<AIResponse | null> {
  if (!anthropicApiKey) {
    console.warn('Anthropic API key not configured');
    return null;
  }

  const client = new Anthropic({
    apiKey: anthropicApiKey,
  });

  try {
    // Create the analysis prompt
    const fullPrompt = `${prompt}

Please search for current, factual information to answer this question. Focus on recent data and real user opinions about ${brandName} and its competitors: ${competitors.join(', ')}.

After searching, analyze your response and determine:
1. Is ${brandName} mentioned? (even if not ranked)
2. What position/rank does ${brandName} have (if any)?
3. What is the sentiment towards ${brandName}?
4. Which competitors are mentioned and their positions?`;

    // Track API call for web search analysis
    const callId = apiUsageTracker.trackCall({
      provider: 'anthropic',
      model: 'claude-3-5-sonnet-latest',
      operation: 'analysis',
      success: true,
      metadata: { 
        type: 'web_search',
        brandName,
        competitorsCount: competitors.length
      }
    });

    const startTime = Date.now();
    // Call Anthropic with web search tool
    const message = await client.messages.create({
      model: 'claude-3-5-sonnet-latest', // This model supports web search
      max_tokens: 1024,
      messages: [
        {
          role: 'user',
          content: fullPrompt,
        }
      ],
      tools: [{
        type: 'web_search_20250305',
        name: 'web_search',
        max_uses: 3, // Limit searches to control costs
      }],
    });
    const duration = Date.now() - startTime;

    // Update API call with duration and estimated tokens
    apiUsageTracker.updateCall(callId, {
      duration,
      inputTokens: Math.ceil(fullPrompt.length / 4), // Rough estimation
      outputTokens: Math.ceil((message.content[0]?.text?.length || 0) / 4),
      cost: estimateCost('anthropic', 'claude-3-5-sonnet-latest', Math.ceil(fullPrompt.length / 4), Math.ceil((message.content[0]?.text?.length || 0) / 4))
    });

    // Extract the response text and citations
    let responseText = '';
    const webSearchResults: WebSearchResult[] = [];

    for (const content of message.content) {
      if (content.type === 'text') {
        responseText += content.text;
        
        // Extract citations if available
        if ('citations' in content && content.citations) {
          for (const citation of content.citations) {
            if (citation.type === 'web_search_result_location') {
              webSearchResults.push({
                url: citation.url,
                title: citation.title || '',
                cited_text: citation.cited_text,
              });
            }
          }
        }
      }
    }

    // Analyze the response to extract structured data
    const analysis = await analyzeResponseContent(
      responseText,
      brandName,
      competitors,
      client
    );

    return {
      provider: 'Anthropic',
      prompt,
      response: responseText,
      brandMentioned: analysis.brandMentioned,
      brandPosition: analysis.brandPosition,
      competitors: analysis.competitorsMentioned,
      sentiment: analysis.sentiment,
      confidence: analysis.confidence,
      timestamp: new Date()
    };

  } catch (error) {
    console.error('Error with Anthropic web search:', error);
    throw error;
  }
}

/**
 * Analyze the response content to extract structured information
 */
async function analyzeResponseContent(
  responseText: string,
  brandName: string,
  competitors: string[],
  client: Anthropic
): Promise<{
  brandMentioned: boolean;
  brandPosition?: number;
  competitorsMentioned: string[];
  sentiment: 'positive' | 'neutral' | 'negative';
  confidence: number;
}> {
  try {
    // Use Claude to analyze its own response
    const analysisResponse = await client.messages.create({
      model: 'claude-3-5-haiku-latest', // Use a faster model for analysis
      max_tokens: 500,
      messages: [
        {
          role: 'user',
          content: `Analyze this AI response about ${brandName}:

"${responseText}"

Extract the following information in JSON format:
{
  "brandMentioned": boolean (is ${brandName} mentioned anywhere?),
  "brandPosition": number or null (what rank/position if mentioned),
  "competitorsMentioned": array of competitor names mentioned from: ${competitors.join(', ')},
  "sentiment": "positive" | "neutral" | "negative" (sentiment towards ${brandName}),
  "confidence": number between 0 and 1
}

Only respond with valid JSON, no other text.`,
        }
      ],
    });

    const analysisText = analysisResponse.content[0].type === 'text' ? analysisResponse.content[0].text : '{}';
    const analysis = JSON.parse(analysisText);

    return {
      brandMentioned: analysis.brandMentioned || false,
      brandPosition: analysis.brandPosition || undefined,
      competitorsMentioned: analysis.competitorsMentioned || [],
      sentiment: analysis.sentiment || 'neutral',
      confidence: analysis.confidence || 0.5,
    };

  } catch (error) {
    console.error('Error analyzing response:', error);
    
    // Fallback to basic text analysis
    const textLower = responseText.toLowerCase();
    const brandLower = brandName.toLowerCase();
    
    return {
      brandMentioned: textLower.includes(brandLower),
      brandPosition: undefined,
      competitorsMentioned: competitors.filter(c => 
        textLower.includes(c.toLowerCase())
      ),
      sentiment: 'neutral',
      confidence: 0.3,
    };
  }
}

/**
 * Check if Anthropic web search is available
 */
export function isAnthropicWebSearchAvailable(): boolean {
  return !!process.env.ANTHROPIC_API_KEY;
}

import { NextResponse } from 'next/server';

// Error codes for different error types
export enum ErrorCode {
  // Authentication errors
  UNAUTHORIZED = 'UNAUTHORIZED',
  FORBIDDEN = 'FORBIDDEN',
  INVALID_TOKEN = 'INVALID_TOKEN',
  SESSION_EXPIRED = 'SESSION_EXPIRED',
  
  // Validation errors
  VALIDATION_ERROR = 'VALIDATION_ERROR',
  INVALID_INPUT = 'INVALID_INPUT',
  MISSING_REQUIRED_FIELD = 'MISSING_REQUIRED_FIELD',
  
  // Resource errors
  NOT_FOUND = 'NOT_FOUND',
  ALREADY_EXISTS = 'ALREADY_EXISTS',
  
  // Rate limiting
  RATE_LIMIT_EXCEEDED = 'RATE_LIMIT_EXCEEDED',
  INSUFFICIENT_CREDITS = 'INSUFFICIENT_CREDITS',
  
  // External service errors
  EXTERNAL_SERVICE_ERROR = 'EXTERNAL_SERVICE_ERROR',
  AI_SERVICE_ERROR = 'AI_SERVICE_ERROR',
  PAYMENT_SERVICE_ERROR = 'PAYMENT_SERVICE_ERROR',
  
  // Database errors
  DATABASE_ERROR = 'DATABASE_ERROR',
  CONNECTION_ERROR = 'CONNECTION_ERROR',
  
  // Generic errors
  INTERNAL_ERROR = 'INTERNAL_ERROR',
  BAD_REQUEST = 'BAD_REQUEST',
  METHOD_NOT_ALLOWED = 'METHOD_NOT_ALLOWED',
}

// Base error class
export class ApiError extends Error {
  public readonly statusCode: number;
  public readonly code: ErrorCode;
  public readonly isOperational: boolean;

  constructor(
    message: string,
    statusCode: number,
    code: ErrorCode,
    isOperational = true
  ) {
    super(message);
    this.statusCode = statusCode;
    this.code = code;
    this.isOperational = isOperational;

    Object.setPrototypeOf(this, ApiError.prototype);
  }
}

// Specific error classes
export class AuthenticationError extends ApiError {
  constructor(message = 'Authentication required', code = ErrorCode.UNAUTHORIZED) {
    super(message, 401, code);
  }
}

export class AuthorizationError extends ApiError {
  constructor(message = 'Access denied', code = ErrorCode.FORBIDDEN) {
    super(message, 403, code);
  }
}

export class ValidationError extends ApiError {
  public readonly fields?: Record<string, string>;

  constructor(
    message = 'Validation failed',
    fields?: Record<string, string>,
    code = ErrorCode.VALIDATION_ERROR
  ) {
    super(message, 400, code);
    this.fields = fields;
  }
}

export class NotFoundError extends ApiError {
  constructor(resource = 'Resource', code = ErrorCode.NOT_FOUND) {
    super(`${resource} not found`, 404, code);
  }
}

export class RateLimitError extends ApiError {
  public readonly retryAfter?: number;

  constructor(
    message = 'Rate limit exceeded',
    retryAfter?: number,
    code = ErrorCode.RATE_LIMIT_EXCEEDED
  ) {
    super(message, 429, code);
    this.retryAfter = retryAfter;
  }
}

export class InsufficientCreditsError extends ApiError {
  public readonly creditsRequired?: number;
  public readonly creditsAvailable?: number;

  constructor(
    message = 'Insufficient credits',
    creditsRequired?: number,
    creditsAvailable?: number
  ) {
    super(message, 403, ErrorCode.INSUFFICIENT_CREDITS);
    this.creditsRequired = creditsRequired;
    this.creditsAvailable = creditsAvailable;
  }
}

export class ExternalServiceError extends ApiError {
  public readonly service?: string;

  constructor(
    message: string,
    service?: string,
    code = ErrorCode.EXTERNAL_SERVICE_ERROR
  ) {
    super(message, 503, code);
    this.service = service;
  }
}

export class DatabaseError extends ApiError {
  constructor(message = 'Database operation failed', code = ErrorCode.DATABASE_ERROR) {
    super(message, 500, code, false);
  }
}

// Error response interface
interface ErrorResponse {
  error: {
    message: string;
    code: string;
    statusCode: number;
    timestamp: string;
    fields?: Record<string, string>;
    metadata?: Record<string, any>;
  };
}

// Helper function to create error response
export function createErrorResponse(error: ApiError): ErrorResponse {
  const response: ErrorResponse = {
    error: {
      message: error.message,
      code: error.code,
      statusCode: error.statusCode,
      timestamp: new Date().toISOString(),
    },
  };

  // Add additional fields for specific error types
  if (error instanceof ValidationError && error.fields) {
    response.error.fields = error.fields;
  }

  if (error instanceof RateLimitError && error.retryAfter) {
    response.error.metadata = { retryAfter: error.retryAfter };
  }

  if (error instanceof InsufficientCreditsError) {
    response.error.metadata = {
      creditsRequired: error.creditsRequired,
      creditsAvailable: error.creditsAvailable,
    };
  }

  if (error instanceof ExternalServiceError && error.service) {
    response.error.metadata = { service: error.service };
  }

  return response;
}

// Main error handler function
export function handleApiError(error: unknown): NextResponse {
  // Log error for debugging
  console.error('[API Error]', error);

  // Handle known API errors
  if (error instanceof ApiError) {
    const errorResponse = createErrorResponse(error);
    return NextResponse.json(errorResponse, { status: error.statusCode });
  }

  // Handle Autumn/Stripe errors
  if (error && typeof error === 'object' && 'response' in error) {
    const responseError = error as any;
    if (responseError.response?.data?.error) {
      const message = responseError.response.data.error.message || 'External service error';
      const externalError = new ExternalServiceError(message, 'payment');
      return NextResponse.json(createErrorResponse(externalError), { status: 503 });
    }
  }

  // Handle generic errors
  if (error instanceof Error) {
    // Check for specific error messages
    if (error.message.includes('Unauthorized') || error.message.includes('unauthorized')) {
      const authError = new AuthenticationError();
      return NextResponse.json(createErrorResponse(authError), { status: 401 });
    }

    if (error.message.includes('Database') || error.message.includes('ECONNREFUSED')) {
      const dbError = new DatabaseError();
      return NextResponse.json(createErrorResponse(dbError), { status: 500 });
    }

    // Generic error
    const genericError = new ApiError(
      process.env.NODE_ENV === 'production' 
        ? 'An unexpected error occurred' 
        : error.message,
      500,
      ErrorCode.INTERNAL_ERROR,
      false
    );
    return NextResponse.json(createErrorResponse(genericError), { status: 500 });
  }

  // Unknown error type
  const unknownError = new ApiError(
    'An unexpected error occurred',
    500,
    ErrorCode.INTERNAL_ERROR,
    false
  );
  return NextResponse.json(createErrorResponse(unknownError), { status: 500 });
}

// Utility function to wrap async route handlers
export function withErrorHandler<T extends any[], R>(
  handler: (...args: T) => Promise<R>
): (...args: T) => Promise<R | NextResponse> {
  return async (...args: T) => {
    try {
      return await handler(...args);
    } catch (error) {
      return handleApiError(error);
    }
  };
}

/**
 * SystÃ¨me de tracking centralisÃ© pour les appels API externes
 * Permet de calculer les coÃ»ts et d'afficher un rÃ©sumÃ© dÃ©taillÃ©
 */

import { logger } from './logger';

export interface ApiCall {
  id: string;
  provider: string;
  model: string;
  operation: 'scrape' | 'competitor_search' | 'prompt_generation' | 'analysis' | 'brand_canonicalization' | 'brand_cleaning' | 'brand_extraction' | 'structured_analysis';
  phase: 'url_analysis' | 'competitor_search' | 'prompt_generation' | 'prompt_analysis' | 'result_analysis';
  timestamp: Date;
  inputTokens?: number;
  outputTokens?: number;
  cost?: number;
  duration?: number;
  success: boolean;
  error?: string;
  metadata?: Record<string, any>;
}

export interface ApiUsageSummary {
  totalCalls: number;
  totalCost: number;
  totalDuration: number;
  byProvider: Record<string, {
    calls: number;
    cost: number;
    tokens: { input: number; output: number };
  }>;
  byOperation: Record<string, {
    calls: number;
    cost: number;
    providers: string[];
  }>;
  byPhase: Record<string, {
    calls: number;
    cost: number;
    duration: number;
    providers: string[];
    averageCostPerCall?: number;
    promptAnalysis?: {
      totalPrompts: number;
      averageCostPerPrompt: number;
    };
  }>;
  errors: number;
}

class ApiUsageTracker {
  private calls: ApiCall[] = [];
  private currentAnalysisId: string | null = null;

  /**
   * DÃ©termine la phase d'un appel basÃ© sur l'opÃ©ration et les mÃ©tadonnÃ©es
   */
  private getPhaseFromCall(call: ApiCall): string {
    // Phase basÃ©e sur l'opÃ©ration
    switch (call.operation) {
      case 'scrape':
        return 'url_analysis';
      case 'competitor_search':
        return 'competitor_search';
      case 'prompt_generation':
        return 'prompt_generation';
      case 'analysis':
        return 'prompt_analysis';
      case 'brand_canonicalization':
      case 'brand_cleaning':
      case 'brand_extraction':
      case 'structured_analysis':
        return 'result_analysis';
      default:
        return 'result_analysis';
    }
  }

  /**
   * DÃ©marre un nouveau tracking d'analyse
   */
  startAnalysis(analysisId: string) {
    this.currentAnalysisId = analysisId;
    logger.info(`[ApiUsageTracker] DÃ©but du tracking pour l'analyse: ${analysisId}`);
  }

  /**
   * Enregistre un appel API
   */
  trackCall(call: Omit<ApiCall, 'id' | 'timestamp' | 'phase'>): string {
    const id = `${call.provider}-${call.operation}-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    const apiCall: ApiCall = {
      ...call,
      id,
      timestamp: new Date(),
      phase: this.getPhaseFromCall(call as ApiCall)
    };
    
    this.calls.push(apiCall);
    
    logger.debug(`[ApiUsageTracker] Appel enregistrÃ©:`, {
      id,
      provider: call.provider,
      model: call.model,
      operation: call.operation,
      tokens: { input: call.inputTokens, output: call.outputTokens },
      success: call.success
    });
    
    return id;
  }

  /**
   * Met Ã  jour un appel existant
   */
  updateCall(id: string, updates: Partial<ApiCall>) {
    const callIndex = this.calls.findIndex(call => call.id === id);
    if (callIndex !== -1) {
      this.calls[callIndex] = { ...this.calls[callIndex], ...updates };
      logger.debug(`[ApiUsageTracker] Appel mis Ã  jour: ${id}`, updates);
    }
  }

  /**
   * Calcule le rÃ©sumÃ© des coÃ»ts
   */
  getSummary(): ApiUsageSummary {
    const summary: ApiUsageSummary = {
      totalCalls: this.calls.length,
      totalCost: 0,
      totalDuration: 0,
      byProvider: {},
      byOperation: {},
      byPhase: {},
      errors: 0
    };

    this.calls.forEach(call => {
      // CoÃ»t total
      if (call.cost) {
        summary.totalCost += call.cost;
      }

      // DurÃ©e totale
      if (call.duration) {
        summary.totalDuration += call.duration;
      }

      // Erreurs
      if (!call.success) {
        summary.errors++;
      }

      // Par provider
      if (!summary.byProvider[call.provider]) {
        summary.byProvider[call.provider] = {
          calls: 0,
          cost: 0,
          tokens: { input: 0, output: 0 }
        };
      }
      summary.byProvider[call.provider].calls++;
      if (call.cost) summary.byProvider[call.provider].cost += call.cost;
      if (call.inputTokens) summary.byProvider[call.provider].tokens.input += call.inputTokens;
      if (call.outputTokens) summary.byProvider[call.provider].tokens.output += call.outputTokens;

      // Par opÃ©ration
      if (!summary.byOperation[call.operation]) {
        summary.byOperation[call.operation] = {
          calls: 0,
          cost: 0,
          providers: []
        };
      }
      summary.byOperation[call.operation].calls++;
      if (call.cost) summary.byOperation[call.operation].cost += call.cost;
      if (!summary.byOperation[call.operation].providers.includes(call.provider)) {
        summary.byOperation[call.operation].providers.push(call.provider);
      }

      // Par phase
      const phase = this.getPhaseFromCall(call);
      if (!summary.byPhase[phase]) {
        summary.byPhase[phase] = {
          calls: 0,
          cost: 0,
          duration: 0,
          providers: []
        };
      }
      summary.byPhase[phase].calls++;
      if (call.cost) summary.byPhase[phase].cost += call.cost;
      if (call.duration) summary.byPhase[phase].duration += call.duration;
      if (!summary.byPhase[phase].providers.includes(call.provider)) {
        summary.byPhase[phase].providers.push(call.provider);
      }
    });

    // Calculer les moyennes et mÃ©triques spÃ©ciales
    Object.keys(summary.byPhase).forEach(phase => {
      const phaseData = summary.byPhase[phase];
      phaseData.averageCostPerCall = phaseData.calls > 0 ? phaseData.cost / phaseData.calls : 0;

      // MÃ©triques spÃ©ciales pour l'analyse des prompts
      if (phase === 'prompt_analysis') {
        // Compter le nombre de prompts uniques analysÃ©s
        const uniquePrompts = new Set();
        this.calls
          .filter(call => this.getPhaseFromCall(call) === 'prompt_analysis')
          .forEach(call => {
            // Essayer diffÃ©rentes clÃ©s pour trouver le prompt
            const prompt = call.metadata?.prompt || 
                          call.metadata?.promptText || 
                          call.metadata?.promptText?.substring(0, 100) + '...';
            if (prompt) {
              uniquePrompts.add(prompt);
            }
          });
        
        // Si on n'a pas trouvÃ© de prompts uniques, estimer basÃ© sur le nombre d'appels et providers
        let totalPrompts = uniquePrompts.size;
        if (totalPrompts === 0) {
          // Estimation: nombre d'appels divisÃ© par le nombre de providers (car chaque prompt est analysÃ© par chaque provider)
          const providers = new Set();
          this.calls
            .filter(call => this.getPhaseFromCall(call) === 'prompt_analysis')
            .forEach(call => providers.add(call.provider));
          
          totalPrompts = Math.max(1, Math.floor(phaseData.calls / Math.max(1, providers.size)));
        }
        
        phaseData.promptAnalysis = {
          totalPrompts,
          averageCostPerPrompt: phaseData.cost / totalPrompts
        };
      }
    });

    // Initialiser les phases manquantes pour un affichage cohÃ©rent
    const phaseKeys = ['url_analysis','competitor_search','prompt_generation','prompt_analysis','result_analysis'] as const;
    phaseKeys.forEach((key) => {
      if (!summary.byPhase[key]) {
        summary.byPhase[key] = {
          calls: 0,
          cost: 0,
          duration: 0,
          providers: [],
