/**
 * Extract brands from text (migrated from brand-detection-enhanced)
 * This function provides the same interface as the old enhanced version
 */
export async function extractBrandsFromText(
  text: string,
  targetBrands: Array<{
    original: string;
    cleaned: string;
    variations: string[];
    reasoning?: string;
  }>,
  provider: string
): Promise<{
  mentionedBrands: Array<{
    brand: string;
    matchedVariation: string;
    confidence: number;
    context?: string;
  }>;
  totalBrandsFound: number;
  confidence: number;
}> {
  console.log(`[BrandDetection] ðŸ” Extraction des marques du texte ${provider} (${text.length} chars)`);

  const mentionedBrands: Array<{
    brand: string;
    matchedVariation: string;
    confidence: number;
    context?: string;
  }> = [];
  const brandNames = targetBrands.map(b => b.cleaned);

  try {
    const detectionResults = await detectMultipleBrands(text, brandNames, {
      caseSensitive: false,
      excludeNegativeContext: false,
      minConfidence: 0.3
    });

    detectionResults.forEach((result, brandName) => {
      if (result.mentioned) {
        result.matches.forEach(match => {
          mentionedBrands.push({
            brand: brandName,
            matchedVariation: match.variation,
            confidence: match.confidence,
            context: text.substring(Math.max(0, match.index - 20), Math.min(text.length, match.index + match.text.length + 20))
          });
        });
      }
    });

    const totalBrandsFound = mentionedBrands.length;
    const confidence = totalBrandsFound > 0 
      ? mentionedBrands.reduce((sum, m) => sum + m.confidence, 0) / totalBrandsFound
      : 0;

    return {
      mentionedBrands,
      totalBrandsFound,
      confidence
    };
  } catch (error) {
    console.error('Brand extraction failed:', error);
    return {
      mentionedBrands: [],
      totalBrandsFound: 0,
      confidence: 0
    };
  }
}

/**
 * Calculate brand visibility by provider (migrated from brand-detection-enhanced)
 * This function provides the same interface as the old enhanced version
 */
export function calculateBrandVisibilityByProvider(
  brandExtractions: Map<string, Array<{
    mentionedBrands: Array<{
      brand: string;
      matchedVariation: string;
      confidence: number;
      context?: string;
    }>;
    totalBrandsFound: number;
    confidence: number;
  }>>,
  targetBrand: string,
  competitors: string[]
): Map<string, Map<string, { 
  mentioned: boolean; 
  confidence: number; 
  mentionCount: number; 
  totalResponses: number; 
  percentage: number 
}>> {
  console.log(`[BrandDetection] ðŸ“Š Calcul des dÃ©tections par provider`);

  const results = new Map();
  const allBrands = [targetBrand, ...competitors];

  // Normalization function for brand comparison
  const normalize = (value: string) => {
    return (value || '')
      .toLowerCase()
      .normalize('NFD')
      .replace(/\p{Diacritic}/gu, '')
      .replace(/[^a-z0-9]+/g, ' ')
      .trim();
  };

  const isSameBrand = (a: string, b: string) => {
    const na = normalize(a);
    const nb = normalize(b);
    if (!na || !nb) return false;
    if (na === nb) return true;
    return na.includes(nb) || nb.includes(na);
  };

  brandExtractions.forEach((extractions, provider) => {
    const providerResults = new Map();
    const totalResponses = extractions.length;

    allBrands.forEach(brand => {
      let mentionCount = 0;
      let totalConfidence = 0;

      extractions.forEach(extraction => {
        const mention = extraction.mentionedBrands.find(m => isSameBrand(m.brand, brand));
        if (mention) {
          mentionCount++;
          totalConfidence += mention.confidence;
        }
      });

      const mentioned = mentionCount > 0;
      const averageConfidence = mentionCount > 0 ? totalConfidence / mentionCount : 0;
      const percentage = totalResponses > 0 ? (mentionCount / totalResponses) * 100 : 0;

      providerResults.set(brand, { 
        mentioned, 
        confidence: averageConfidence,
        mentionCount,
        totalResponses,
        percentage: Math.round(percentage * 10) / 10
      });
    });

    results.set(provider, providerResults);
  });

  return results;
}

/**
 * Brand highlighting utilities for text processing
 * Extracted from HighlightedResponse component for better reusability
 */

import React from 'react';

export interface BrandHighlightingConfig {
  targetBrand: string;
  competitors: string[];
  targetHighlightClass: string;
  competitorHighlightClass: string;
  defaultHighlightClass: string;
}

export interface BrandDetectionResultForHighlighting {
  mentioned: boolean;
  matches: Array<{
    text: string;
    index: number;
    brandName: string;
    variation: string;
    confidence: number;
  }>;
  confidence: number;
}

/**
 * Get the appropriate CSS class for a brand based on its type
 */
export function getBrandHighlightClass(
  brandName: string,
  config: BrandHighlightingConfig
): string {
  const normalizedBrand = brandName.toLowerCase();
  const normalizedTarget = config.targetBrand.toLowerCase();
  const competitorSet = new Set(config.competitors.map((c) => c.toLowerCase()));

  if (normalizedBrand === normalizedTarget) {
    return config.targetHighlightClass;
  }
  if (competitorSet.has(normalizedBrand)) {
    return config.competitorHighlightClass;
  }
  return config.defaultHighlightClass;
}

/**
 * Create highlighted HTML for a text based on brand detection results
 */
export function createHighlightedHtml(
  text: string,
  detectionResults: Map<string, BrandDetectionResultForHighlighting>,
  config: BrandHighlightingConfig
): string {
  let highlightedText = text;

  detectionResults.forEach((result, brandName) => {
    if (!result.mentioned || result.matches.length === 0) return;

    // Get unique variations for this brand (use the actual matched texts)
    const variations = [...new Set(result.matches.map((m) => m.text))];
    const className = getBrandHighlightClass(brandName, config);

    variations.forEach((variation) => {
      const escaped = variation.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
      const regex = new RegExp(`\\b${escaped}\\b`, 'gi');

      highlightedText = highlightedText.replace(
        regex,
        (match) =>
          `<span class="${className}" data-brand-highlight="true" data-brand-name="${brandName}">${match}</span>`
      );
    });
  });

  return highlightedText;
}

/**
 * Highlight text with brand mentions using React components
 */
export function highlightTextWithBrands(
  text: string,
  detectionResults: Map<string, BrandDetectionResultForHighlighting>,
  config: BrandHighlightingConfig,
  showHighlighting: boolean = true
): React.ReactNode {
  // Early returns for edge cases
  if (!showHighlighting || !text || text.trim().length < 3 || detectionResults.size === 0) {
    return text;
  }

  const highlightedText = createHighlightedHtml(text, detectionResults, config);

  // Return original text if no highlighting was applied
  if (highlightedText === text) {
    return text;
  }

  return <span dangerouslySetInnerHTML={{ __html: highlightedText }} />;
}

/**
 * Recursively highlight brand mentions in React children (for markdown processing)
 */
export function highlightMarkdownChildren(
  children: React.ReactNode,
  detectionResults: Map<string, BrandDetectionResultForHighlighting>,
  config: BrandHighlightingConfig,
  showHighlighting: boolean = true
): React.ReactNode {
  return React.Children.map(children, (child) => {
    // Process text nodes
    if (typeof child === 'string') {
      return child.trim().length > 0
        ? (highlightTextWithBrands(
            child,
            detectionResults,
            config,
            showHighlighting
          ) as React.ReactNode)
        : child;
    }

    if (typeof child === 'number') {
      return highlightTextWithBrands(String(child), detectionResults, config, showHighlighting);
    }

    // Process React elements
    if (React.isValidElement(child)) {
      // Skip already processed elements
      if ((child.props as any)?.['data-brand-highlight']) {
        return child;
      }

      // Recursively process children
      if ((child.props as any)?.children) {
        return React.cloneElement(child as any, {
          children: highlightMarkdownChildren(
            (child.props as any).children,
            detectionResults,
            config,
            showHighlighting
          ),
        });
      }

      return child;
    }

    // Process arrays
    if (Array.isArray(child)) {
      return highlightMarkdownChildren(child, detectionResults, config, showHighlighting);
    }

    return child;
  });
}

// Client-side error parsing utility

export interface ApiErrorResponse {
  error: {
    message: string;
    code: string;
    statusCode: number;
    timestamp: string;
    fields?: Record<string, string>;
    metadata?: Record<string, any>;
  };
}

export class ClientApiError extends Error {
  public readonly code: string;
  public readonly statusCode: number;
  public readonly timestamp: Date;
  public readonly fields?: Record<string, string>;
  public readonly metadata?: Record<string, any>;

  constructor(errorResponse: ApiErrorResponse) {
    super(errorResponse.error.message);
    this.code = errorResponse.error.code;
    this.statusCode = errorResponse.error.statusCode;
    this.timestamp = new Date(errorResponse.error.timestamp);
    this.fields = errorResponse.error.fields;
    this.metadata = errorResponse.error.metadata;

    Object.setPrototypeOf(this, ClientApiError.prototype);
  }

  // Helper methods for common error checks
  isAuthenticationError(): boolean {
    return this.statusCode === 401 || this.code === 'UNAUTHORIZED';
  }

  isValidationError(): boolean {
    return this.statusCode === 400 && this.code === 'VALIDATION_ERROR';
  }

  isRateLimitError(): boolean {
    return this.statusCode === 429 || this.code === 'RATE_LIMIT_EXCEEDED';
  }

  isInsufficientCreditsError(): boolean {
    return this.code === 'INSUFFICIENT_CREDITS';
  }

  isNotFoundError(): boolean {
    return this.statusCode === 404 || this.code === 'NOT_FOUND';
  }

  isServerError(): boolean {
    return this.statusCode >= 500;
  }

  // Get user-friendly error message
  getUserMessage(): string {
    // Special handling for common errors
    switch (this.code) {
      case 'UNAUTHORIZED':
        return 'Please log in to continue';
      case 'SESSION_EXPIRED':
        return 'Your session has expired. Please log in again';
      case 'INSUFFICIENT_CREDITS':
        return `You need ${this.metadata?.creditsRequired || 'more'} credits. You have ${this.metadata?.creditsAvailable || 0} credits available`;
      case 'RATE_LIMIT_EXCEEDED':
        return `Too many requests. Please try again in ${this.metadata?.retryAfter || 60} seconds`;
      case 'VALIDATION_ERROR':
        if (this.fields && Object.keys(this.fields).length > 0) {
          return 'Please check the highlighted fields';
        }
        return this.message;
      case 'EXTERNAL_SERVICE_ERROR':
        return `Service temporarily unavailable${this.metadata?.service ? ` (${this.metadata.service})` : ''}. Please try again later`;
      case 'DATABASE_ERROR':
        return 'Unable to complete the operation. Please try again';
      default:
        return this.message;
    }
  }
}

// Parse API response and throw ClientApiError if it's an error
export async function parseApiResponse<T>(response: Response): Promise<T> {
  if (!response.ok) {
    try {
      const errorData = await response.json() as ApiErrorResponse;
      throw new ClientApiError(errorData);
    } catch (error) {
      if (error instanceof ClientApiError) {
        throw error;
      }
      // Fallback for non-standard error responses
      throw new ClientApiError({
        error: {
          message: 'An unexpected error occurred',
          code: 'UNKNOWN_ERROR',
          statusCode: response.status,
          timestamp: new Date().toISOString(),
        },
      });
    }
  }

  return response.json();
}

// Hook for handling API errors in React components
export function useApiError() {
  const handleError = (error: unknown): string => {
    if (error instanceof ClientApiError) {
      return error.getUserMessage();
    }
    if (error instanceof Error) {
      return error.message;
    }
    return 'An unexpected error occurred';
  };

  return { handleError };
}

